-- ----------------------------
-- Table structure for `articles`
-- ----------------------------
DROP TABLE IF EXISTS `articles`;
CREATE TABLE `articles` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增ID',
  `category_id` int(11) NOT NULL COMMENT '类别ID',
  `title` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT '文章标题',
  `summary` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '文章描述',
  `thumbnail` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '缩略图',
  `image` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '详情大图',
  `content` text COLLATE utf8_unicode_ci NOT NULL COMMENT '文章内容',
  `keywords` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '关键字',
  `source` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT '来源',
  `author` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT '责任编辑',
  `views` int(11) NOT NULL COMMENT '阅读量',
  `recommend` int(11) NOT NULL COMMENT '推荐（0-未推荐，1-推荐）',
  `status` int(11) NOT NULL DEFAULT '0' COMMENT '状态（0-创建未审核，1-已审核，2-已发布',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=84 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
-- Records of articles
-- ----------------------------
INSERT INTO `articles` VALUES ('1', '2', '色彩缤纷马卡龙“染色”食品你敢吃吗？', '马卡龙，是法国19世纪以来就有出产的蛋白杏仁饼，用蛋白、杏仁粉、白砂糖和糖霜等制成，通常在两块饼干之间夹有水果酱或奶油等内馅，在海内外都极具盛名。马卡龙近年来以其缤纷的色彩、细腻清新的口感和小巧玲珑的外形，博得了女生们的芳心。', 'http://o9sccet84.bkt.clouddn.com/20160718092030299.png', '', '<p style=\"text-indent: 2em;\">马卡龙，是法国19世纪以来就有出产的蛋白杏仁饼，用蛋白、杏仁粉、白砂糖和糖霜等制成，通常在两块饼干之间夹有水果酱或奶油等内馅，在海内外都极具盛名。马卡龙近年来以其缤纷的色彩、细腻清新的口感和小巧玲珑的外形，博得了女生们的芳心。无论是阳光明媚的午后和三五好友共享悠闲时光，还是周末在家为亲爱的制作一盘精致甜点拴住他的胃，马卡龙似乎都是不二选择。无论实体店购买还是网络代购，马卡龙绝对是价格不菲，加上运费，5个装售价160元以上，10个装售价接近300元。</p><p style=\"text-indent: 2em;\">2012年，国家质检总局公布了2011年12月份进口不合格食品名单，名单中由法国知名食品公司Jean paul HAVIN Chocolatier生产的“芒果味马卡龙饼”、“百香果马卡龙饼”，皆被检出“违规使用化学物质日落黄、胭脂红、胭脂虫红、酸性红、柠檬黄”。 Jean paul HAVIN Chocolatier生产的马卡龙饼曾在上海、香港开设专门店，不少广州、深圳市民都曾慕名赴港购买。食品添加剂专家、广东省食文化研究会会长杨冠丰表示，当中日落黄、胭脂红和柠檬黄等，都属于色素添加剂。</p><p style=\"text-indent: 2em;\">说起马卡龙的靓丽色彩究竟从哪里来，的确是个尴尬的问题：由于马卡龙的制作过程中都不能掺有水分，所以各种色彩缤纷的颜色也都是以食用色膏染色而成，不能拿水果榨成汁或选取果酱等染色，因为这样马卡龙的口感就完全不是经典的感觉。</p><p><br/></p>', '添加剂，马卡龙', '', 'admin', '0', '0', '2', '2016-07-15 16:40:36', '2016-07-21 17:03:16');
INSERT INTO `articles` VALUES ('2', '16', '王贵怀：最大的挑战来自自己', '在王贵怀心目中，手术刀并非一件冷兵器，它是有温度的。\n\n“手术刀握在手中，刀下是纵横交织的血管、神经，和隐藏在其中的病变。这个时候，你会觉得这枚柳叶大小的刀是有生命的，因为它面对的，是生命。”王贵怀认真地说。\n\n王贵怀身材中等，手却极白晳小巧，柔若无骨，是理想的外科医生的手。在办公室接受记者采访的两个多小时里，王贵怀把他的手十指交叉安放在膝盖上，没有一个冗余的动作。\n\n早在12年前，时年38岁的王', 'http://o9sccet84.bkt.clouddn.com/20160715170025457.jpg', '', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>做外科医生需要有天赋</strong></p><p><br style=\"color: rgb(89, 87, 87); font-family: Simsun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"/><br style=\"color: rgb(89, 87, 87); font-family: Simsun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“外科医生做手术需要天赋。”王贵怀说。他刚刚做完了一台手术，淡绿色的手术服还没来得及换掉。“在病变和正常组织之间的缝隙里，能够让医生施展的空间很小很小，要格外的精细，一两毫米的误差或稍稍大一点的动作，就可能误伤到神经，病人就会瘫痪。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">不同的外科医生面对手术时，会有不同的态度。第一种人，胆子比较小，做手术时战战兢兢，浅尝辄止；还有一种人胆子大，行事鲁莽，一旦造成大出血，后果就不堪设想；第三种人，既能把手术做的接近完美，又能想尽方法规避术中可能发生的风险。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“这要靠智慧和勇气。”王贵怀说，有的医生面对术中可能出现的各种意外，比如大出血时，会紧张得一下子坐到地上去，“而如果主刀者沉着冷静，能准确找到出血的地方，就会用最恰当的方法快速把血止住，反应要非常迅速不能拖泥带水。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“这种人往往需要天赋。”王贵怀强调，“这是能反映一个人悟性的东西，是没人能够教给你的。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">作为中国工程院院士王忠诚的得意弟子，12年前的那台手术，让王贵怀向老师和世人展现了自己的悟性和天赋。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">当时年仅14岁的男孩小涛，由于髓内肿瘤已瘫痪，坐着轮椅四处求医，得到的回答异口同声：这个手术很危险，做完以后有可能影响到呼吸，甚至危及生命。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“就这样，病情被延误下来，他找到我的时候，肿瘤已达26公分长，这是非常罕见的巨型髓内肿瘤。”王贵怀当时已在天坛医院工作了16年，做过很多复杂的髓内肿瘤手术，都很成功。“我认为这个手术应该可以做，未必做完就不行。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“我要挑战自己！”王贵怀很快下定决心：“同时，也是给小涛一个活下去的机会。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">收下病人后，新的问题接踵而至——为了治病，小涛家早已家徒四壁。王贵怀想方设法找到了贵阳市脑科医院，“夏院长非常给力，说可以不收费。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">在王贵怀的协调下，小涛很快住进了这家医院，王贵怀随后自己掏钱买了机票，也从北京飞到了贵阳。临走前，他再三请教老师手术的各种注意事项，做好了各项预案。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>征服自己</strong></p><p><br style=\"color: rgb(89, 87, 87); font-family: Simsun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"/><br style=\"color: rgb(89, 87, 87); font-family: Simsun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“2003年10月15日，杨利伟乘飞船征服太空的同时，我也在征服自己。小涛的手术从早上8点半一直做到晚上11点钟，做了将近15个小时。”王贵怀说。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">从生命中枢脑干长出的肿瘤，一直延伸到了小涛的胸椎，肿瘤塞满了髓腔，脊髓被压迫呈薄皮状。王贵怀需要做的是，摘除肿瘤，同时不能碰到这层薄皮，况且其间分布着密密麻麻的血管和神经。薄皮的任何破损，都将导致神经的损坏，血管破裂的风险则可能让小涛直接殒命。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“从第一刀起，到最后一刀落下，都要保持着一样的精准、步调、呼吸、体力，还有心境。”王贵怀说。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">15个小时过去了，手术宣告成功！王贵怀仍不敢懈怠，认真观察小涛，直到看到他睁眼了，能动了，呼吸正常了，一直高悬的心才慢慢放下。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">这时，时针已经指向第二天的凌晨一点，小涛的情况非常平稳，王贵怀这时才意识到自己整整一天滴水未进，便到医院门口尚未打烊的路边摊上，随便吃了些贵阳的地方小吃，“我都忘了吃的是什么，但记得那真是前所未有的美味。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“又过了一天，小涛的情况越来越好，我就给老师打电话，兴奋地告诉他：手术做完了，病人能动，呼吸机不上。王院士一听特别欣慰。”王贵怀回忆起当时的情景，仍然十分感慨。 二十多天后，小涛就能下地行走了。术后第1年，王贵怀去广西看望小涛；术后第5年，小涛来到北京给王贵怀送了一面锦旗。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“第10年，我又去广西见了他一面。”王贵怀说，“现在小涛已经是26岁的小伙子了！我仍然保持着对他的随访。在我的从医生涯中，这是一个可遇不可求的病例。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">这台手术是中国神经外科史上的一个“里程碑”，肿瘤之大，难度之高，无人可以企及。手术的整个过程都被完整发表在美国著名的《神经外科》杂志上。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“和老师相比，这些都不算什么。”王贵怀由衷地说：“他是神经外科医生中极少数的做过一万台以上开颅大手术的人，至少挽救过一万个生命，被誉为‘万颅之魂’。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">在办公室的醒目位置，悬挂着王忠诚院士手书的“发展神经外科事业”几个大字，被王贵怀视作老师对自己的鞭策和鼓励。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>尽善尽美的境界</strong></p><p><br style=\"color: rgb(89, 87, 87); font-family: Simsun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"/><br style=\"color: rgb(89, 87, 87); font-family: Simsun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">自1994年成为王忠诚院士的学生，一直到他2012年去世，王贵怀在老师身边待了整整18年。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“刚见到王院士的时候，就感觉他非常慈祥、超然物外，绝对大师级的人物。直到今天，我都觉得自己差好远，远没达到他的境界。”提及恩师，王贵怀话语间满是敬意。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">但在王贵怀救治的患者和同事们的心目中，他早已成为榜样和旗帜。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">看过好莱坞大片的人，对这样的镜头并不陌生：主角须拆除一枚即将被引爆的炸弹，计时器上的时间在迅速消逝，向“0”迫近，观众的心脏随着秒针的滴答声而怦怦直跳，主角则屏住呼吸顶住压力，在密密麻麻的各色电线中快速地找到一根，当机立断，于是，计时器定格在了0.01秒。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“就是这样的境界。”张壮告诉记者，在他眼中，王贵怀就是这样一个主角，在拯救生命的过程中发挥着至关重要的作用。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">一年前，王贵怀为张壮做了脊髓管膜瘤切除术，术后恢复情况非常好。两个多月前，张壮执意要报名参加泰山国际马拉松比赛，当时，他的家人极力劝阻，担心他刚做完手术，身体状况难以承受激烈的赛事带来的压力，但张壮毫不退让，特意找到王贵怀征询他的意见。 “王大夫，我要找女朋友谈恋爱了，我要证明自己！”王贵怀为张壮进行了各项检查后，点了点头。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“很幸运，我用两个小时十一分钟顺利完成了半程项目。”张壮把好消息通过短信告诉王贵怀。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“这是我收到的最好的礼物！”王贵怀由衷地说：“张壮总说很感谢我，其实，是我要感谢他。一是，他生了这么大一场病，仍然这么自强不息，他的生活勇气是我非常敬佩的，我这个健康的人都不敢去跑马拉松；其二，这样的肿瘤手术还能恢复得这样好，虽然跟我们的手术水准有一定的关系，但更要靠他自身的勇气，意志，还有信念！”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“你看，在医治病人的同时，也能让自己的心灵得到净化和升华。你肯定听说过这句话：最好的医生是佛陀，最好的药物是慈悲。”王贵怀告诉记者：“从医快三十年了，救助的病人多了，常会扪心自问：你是最好的医生吗？你能成为一个佛吗？”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">中国外科学奠基人裘法祖说过：德不近佛者不可为医，才不近仙者不可为医。明代裴一中在《言医·序》中也说过:“学不贯今古，识不通天人，才不近仙，心不近佛者，宁耕田织布取衣食，断不可作医以误世”。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">清华长庚医院的院训共八个字：人本，济世，厚德，至善。“我一直追求的是最后两个字——‘至善’，‘至善’就是善到极点。我做每一件事，都要求自己能达到尽善尽美的境界。医生面对的是生命，不像写书法或文章，不满意可以撕掉重写。医疗不是这样的，做手术前，病人各方面的信息要研究得精准透彻，准备得相当充分了，才能动这个手术。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\"><strong>医生应该是有信仰的</strong></p><p><br style=\"color: rgb(89, 87, 87); font-family: Simsun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"/><br style=\"color: rgb(89, 87, 87); font-family: Simsun; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“医生应该是有信仰的——信，是感恩；仰，是敬畏。”王贵怀说：“我们要对患者怀有感恩的心，要敬畏每一个生命。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">正是在这种信念的支撑下，王贵怀收治了很多其他医院其他医生都不愿意收的，病情危重的病人。“我把他们收进来，他们就多了一个活下去的机会。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">清华长庚医院成立刚刚一年，神经外科就已做了460台手术，每一台手术都很成功，除港澳台以外，病人遍布全国每一个省份，包括来自新疆、西藏的病人。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">在病房区一个不显眼的靠墙的柜子里，横七竖八堆满了被卷成筒状的红色锦旗。“这些都是病人送的，实在是放不下了，只好堆在这里。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">病房里住满了病人，而且这些病人基本上都是冲着王贵怀来的，但王贵怀很少标榜自己，他再三强调“现代的手术需要的是一群人”，有负责电子监控的，有超声的，有掌握器械的.....，手术过程中，团队的每个人都非常重要。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“此外，高精尖的设备同样重要。以前，神经外科领域的很多疾病医生是束手无策的。”王贵怀说。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">1952年，天津总医院成立了脑系科，这是中国最早的神经外科科室，由赵以诚教授做科主任，王忠诚院士则是首批住院医生。当时医院常会接到从抗美援朝前线送回来的脑部受伤的战士，医生们毫无办法，只能眼睁睁地看着战士瞳孔变大，昏迷，直至死亡。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“那个时候王院士就下了决心：一定要发展神经外科！”王贵怀说。<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“经济腾飞是神外发展的一个机遇，假如没钱，像核磁等价格非常昂贵的仪器，需要投入的资金量很大，如果医院买不起，神经外科这个科室是开不起来的。”王贵怀指出，近年来由于影像学等科学技术的发展，在做手术时，3D影像就可以实时地捕捉病人各个部位的信息，帮助医生精准地找到病变位置。“对病人而言，创伤更小了，是更加安全了；对医生而言，心里更踏实了。”<br/><br/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; line-height: 22px; text-indent: 2em; color: rgb(89, 87, 87); font-family: Simsun; white-space: normal; background-color: rgb(255, 255, 255);\">“这就是科技的力量。”王贵怀一边说，一边注视着王忠诚院士题写的“发展神经外科事业”几个大字，只有他自己知道，这几个字带给他的力量。<br/></p><p><br/></p>', '王贵怀', '腾讯健康', 'admin', '0', '1', '2', '2016-07-15 17:00:32', '2016-07-21 17:03:11');
INSERT INTO `articles` VALUES ('3', '16', '国际标准细胞制备实验室引领行业升级', '2016年有望成为细胞治疗的爆发年。随着我国将精准医疗纳为十三五重大科技专项，美国及欧洲也可能在政府扶持下进一步加码精准医疗，这预示着除了其本身广阔的应用前景外，细胞治疗将面临来自全球及国内的多重催化。在此背景下，集上游存储、中游研发、下游临床于一身的全产业链布局类企业或将在未来两年内取得突破性进展。\n\n作为我国的一家全产业链布局企业，中赢生命方舟在这样的行业环境下大显身手，旗下高端免疫细胞健康品', 'http://o9sccet84.bkt.clouddn.com/20160715174515406.jpg', '', '<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	<strong>占地1500平方米，相当于3个篮球场大小</strong>\n</p>\n<br />\n<br />\n<br />\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	杭州滨江高新技术开发区，环境优雅，地理位置卓越，是个天然养人的风水宝地。中赢生命方舟细胞银行和它的细胞制备实验室就坐落于此，实验室占地1500平方米，相当于3个篮球场的大小。据中赢生命方舟细胞银行的首席科学家徐以兵博士介绍，中赢生命方舟细胞银行的实验室不仅规模宏大，而且都是严格按照美国FDA现行的GMP标准执行建立的，中赢生命方舟细胞银行内的一切细胞提取、扩增、培养、储存流程都是在实验室内完成的。\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	液氮柜储存是现阶段最先进最流行的细胞储存方式。在中赢生命方舟细胞银行扩增完细胞后，细胞也会储存在零下196摄氏度的液氮柜里，而且这里的液氮柜都是从美国原装进口的，设有实时报警系统，每天早晚还会安排工作人员进行一次液位监测。据介绍，实验室的完全年处理能力达6500人份，基地中心能够同时储存10万人份的全免疫细胞和扩增激活的NK免疫细胞，也就是说，实验室能容纳一个鸟巢体育场所有观众的NK细胞。\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	<strong>三审三核，进出实验室至少耗费半个小时</strong>\n</p>\n<br />\n<br />\n<br />\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	中赢生命方舟细胞银行的实验室分为细胞制备区、检测区、洁净区和纯水站4个区域，据称，这里的每一立方米的空气中，大于5微米的尘埃粒子都不会超过2900个。这是因为实验室的每个角落都安装了送回风系统，每小时高达25次的换气让实验室每个区域的最低净化级别都达到了万级净化的环境标准，如果不相信还可以亲自测试。\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	实验室还设置了专门用来制备纯水的纯水站，制备的纯水用来清洁洁净区和清洗洁净服，经过纯水洗涤清洁后，可以去除98%以上的溶解盐，99%以上的微生物和99%以上的有机体。\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	除了送回风系统和专门的纯水站，实验室对于人员和物料的清洁也进行了严格把控。据徐以兵介绍，实验室里用到的所有物料，都必须经过复检合格之后才能从物料入口进入，而生产废料也必须经过灭活之后才能从废料出口流出实验室。客户的血样也要在样品交接室经过三人复核之后，才能从传递窗进入。所有的工作人员在经过员工通道前，也必须经过一更、二更、缓冲间才能进入到细胞制备区，而且必须按规定穿着四连体的洁净服，洁净服只要从密封包装中取出，就不能二次使用；每次使用完毕之后，都要经过纯水清洁，然后进行密封包装灭菌处理。\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	<strong>造价1个亿，一平方米价值300克黄金</strong>\n</p>\n<br />\n<br />\n<br />\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	打造一个中赢生命方舟细胞银行这样的实验室要花费多少钱？中赢生命方舟细胞银行以专业立身，作为一项高端技术，它的前期投入非常大，研发成本也很高。仅从实验室硬件设备来看，整个实验室就投入了1亿多人民币，平均每一平方米将近7万元。按照黄金交易所的今日金价计算，仅一平方米的造价就约合300克黄金的价值！所以中赢生命方舟细胞银行的实验室可谓寸土寸金！\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	不过，实验室最值得人们津津乐道的并不是它的造价，而是里面各种先进精密的实验仪器。据介绍，中赢生命方舟细胞银行的实验室中所有的设备都是从美国进口的，包括-196℃液氮柜、程序降温仪、流式细胞仪、酶标仪、超净工作台、生化培养箱、尘埃粒子计数器、浮游菌采集器、生物安全柜、二氧化碳培养箱、离心机等多种专业仪器，其中最贵的流式细胞仪仅单价就高达150万元人民币。\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	有了如此雄厚的资金保障，加上各种各样有着美国出生证的实验设备，你难道还不相信中赢生命方舟细胞银行有着足够的硬实力提供最有保证的NK细胞提取和保存技术吗？\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	总而言之一句话，中赢生命方舟细胞银行国内领先细胞制备实验室，专业担当，值得信赖！\n</p>', '腾讯健康频道,专业健康资讯门户网站，提供实用,全面的健康信息,包括中医,保健', '腾讯健康', 'admin', '0', '1', '2', '2016-07-15 17:37:24', '2016-07-21 17:03:06');
INSERT INTO `articles` VALUES ('4', '16', '孟化：减肥手术只是推手，而不是终结者', '“对于过度肥胖的人而言，减肥手术只能解决生命长河中某个时段的问题，而不是全部问题。”北京友谊医院普外科主任医师孟化说。\n \n今年44岁的孟化已做过两百多例减肥手术，两百多名超级巨胖在他的手术刀下减去了赘肉，降低了血压血糖，开始了新生，孟化也因此被患者推崇和热爱。\n \n然而，7月7日晚，忙碌了一天的孟化教授在接受记者采访时却强调：“手术只是健康人生的推手，而不是终结者。”', 'http://o9sccet84.bkt.clouddn.com/20160715174647480.jpg', '', '<p>\n</p>\n<p>\n	<img src=\"/public/admin/kindeditor/attached/image/20160720/20160720145949_83784.jpg\" alt=\"\" />\n</p>\n<p>\n	手术只是一个推手，人的意志力更重要\n</p>\n<p>\n	“主要还是靠自己。”孟化告诉记者：“人一生的健康不可能通过一次手术而拥有，手术只是在你人生的关键时刻推了你一把。”\n</p>\n<p>\n	孟化指出，良好的生活方式、生活习惯是非常重要的，“我可以用手术帮助你从300斤减到200斤，但如果你没有养成良好的习惯，体重会很快反弹。”\n</p>\n<p>\n	有一次，孟化遇到一位刚做完减肥手术后不久的病人，他一见孟化就立即把手背到身后，孟化马上明白了什么，坚持要他把手伸出来，结果发现，他手里攥着的，是一块榛子巧克力。 孟化非常生气，立刻正色告诉这位病人，像这种高热量的食物是不能碰的，不能因为自己作了减肥手术，就可以从此高枕无忧，像以前那样胡吃海喝，如果不节制，不改变原有的喜好，就会前功尽弃。\n</p>\n<p>\n	“手术只是一个推手。”孟化认真地说，“人的意志力更重要。”\n</p>\n<p>\n	为了说明自己的观点，让患者加深印象，孟化经常给大家讲述米兰提夫和史泰隆的故事。 前世界三级跳远冠军米兰提夫，8岁时患了小儿麻痹，但他并不为疾病所累，坚持自己学走、学跑，研究各种符合自己身体条件的跳远姿势，终于夺得了世界冠军。\n</p>\n<p>\n	米兰提夫回答成功的秘诀时说：“当我参加比赛时，一般人都在看我跳远当时的表现，其实，任何运动比赛的成功，不单决定于他表现的那个时刻，重要的是，决定于他表现之前所做的准备。” “对于减肥患者而言，手术后的各项准备，比如饮食习惯生活习惯等，是起决定作用的。此外，史泰隆的故事同样励志。”孟化说：“立志当演员的他曾因咬字不清屡遭拒绝，后来推销他自己写的剧本时，又被各个电影公司老板们拒绝了1855次，但第1856次，他成功了。”\n</p>\n<p>\n	“手术后，只有坚持良好的习惯，才能真正改变人生。”孟化得出结论。\n</p>\n<p>\n	<br />\n</p>\n<p>\n	管住嘴比迈开腿更重要：不喝甜饮料，减少淀粉摄入\n</p>\n<p>\n	俗话说，减肥只需要六个字：管住嘴，迈开腿。“其实，管住嘴比迈开腿更重要。”孟化说。\n</p>\n<p>\n	“出行开车，有空时就玩手机，人们运动的时间大大减少了。”孟化指出，通过运动消耗的热量，所产生的减重效果是有限的，何况肥胖的人由于体重大，过度运动有可能导致关节的损伤。\n</p>\n<p>\n	“肥胖产生的原因是由于高热量食物的摄取，尤其是饮料。”孟化说，一瓶甜饮料所含的热量约为250大卡，而人体一天所需的热量是1200－1800大卡，“如果一天喝上五六瓶饮料，就不需要再吃任何东西了。”\n</p>\n<p>\n	人在摄入甜的食物时会产生愉悦的感受，因此厂家在生产饮料时，会添加糖分以改善口感。殊不知，喝着超爽的饮料，热量却随之超标。\n</p>\n<p>\n	“绿茶，红茶，红牛，脉动等饮料，最好都要节制。”孟化推荐大家饮用零卡路里的矿泉水和纯净水，“取自天然的水，是最好的饮品。”\n</p>\n<p>\n	孟化非常重视对患者的健康教育，日前，他在其公众订阅号“孟化医学在线”上，发表了一篇文章指出，英国政府专家的研究表明，吃饭的时候别喝甜饮料，以防过量摄入糖分，造成安全隐患。\n</p>\n<p>\n	<br />\n</p>\n<p>\n	含糖饮料有害健康，而果汁的危害与之不相上下。比如，一杯250毫升的橙汁含有115卡路里的热量，只比一罐可乐低24卡路里。人们只是片面地看重果汁的营养成分，却忽略了随之而来的肥胖症、糖尿病和心脏疾病的患病风险。\n</p>\n<p>\n	除了饮料，孟化认为，中国人对淀粉类食物的偏爱也是导致肥胖的重要因素之一。“山药，土豆，红薯，芋头，米饭，馒头等，这些食物都富含淀粉。”\n</p>\n<p>\n	需要指出的是，一旦远离白饭、面条、面包、水饺等淀粉类食物，用不了几天，就会出现头昏眼花、四肢无力的情形，就会忍不住到厨房耄耋一番。 “所以要科学饮食。”孟化介绍说，胖子一般都是美食家，还都喜欢亲自动手做各种食物，比如肘子、红烧肉等，因此，医院正准备安排专职人员，随时对术后患者的饮食、运动进行引导，“我们把这个岗位叫个案管理师。”\n</p>\n<p>\n	因为减肥而改变的人生\n</p>\n<p>\n	“手术后，她们改变的不仅仅是体重。”孟化非常欣慰地说：“还有人生。”\n</p>\n<p>\n	刚出院不久的一个姑娘，身高一米六五，手术前是240斤，现在是150斤，已经趋于正常体重了，就连原来特别不正常的例假都正常了。“这是个颜值特别高的女孩，应该有九十分以上，以前就是因为太胖而被人笑话，现在瘦下来了，她一下子找回了自信！常常给我们发她的自拍照。”孟化高兴地说。\n</p>\n<p>\n	还有一个十七岁的少女，原先是200斤，从小就因为太胖被人嘲笑冷落，遇到别人要拍照合影时总是默默地躲开，但现在情况发生了逆转，现在的她非常喜欢嘟嘴卖萌，对拍照也不排斥了，因为她现在120斤，已经算是正常体重。“她也通过减重找回了自信！”孟化说。\n</p>\n<p>\n	“这样的例子实在太多了！”说起患者们手术前后的变化，孟化特别开心，这让他特别有成就感。\n</p>\n<p>\n	而在患者的眼中，孟化的好口碑也是一传十，十传百，“现在找我做手术的病人，百分之八十都是我的患者介绍来的。”\n</p>\n<p>\n	6月10日是孟化的生日，患者们都吵着要给他过生日，让原本忙得忘掉了这个日子的他十分感动。\n</p>\n<p>\n	患者们给孟化和他的团队的评价出奇地一致：“孟化主任给人的感觉是文质彬彬，非常亲和。以‘高帅’为特征的孟主任医疗小组成员们，每天忙碌的程度是非常规职业能比的：早8点到10点半间定时的查房，一天1到3场的手术，下午又是不定时的查房，中间还伴有教学任务、专家会诊、门诊安排、病患问诊等等”。\n</p>\n<p>\n	还有一位患者在网上留言说：“有一天我上午10点多发帖寻问病情，晚上8点多就得到了孟大夫的回复，这样的速度真出乎我的意料。孟大夫在百忙之中，能够迅速地回复，让我对病情有个真实的了解，我感到很欣慰。真的很感激！”\n</p>\n<p>\n	在某网站上患者给医生们的评分中，孟化的医术和医德的得分都是100分，这让孟化非常意外。“我非常重视和患者的沟通，基本没有发生过医疗纠纷，但大家给我这么高的评价，我还是挺受激励的。” 在孟化的办公桌上，放着一幅他正策马飞奔的照片，背景是一望无垠的绿色大草原，照片中的这一爿绿意，点亮了他忙碌的工作，让他仿佛触摸到了忙碌背后的意义。\n</p>\n<p>\n	“手术只能解决某个时段的问题，而不是全部问题。”孟化认真地告诉记者：“这不是医学，是哲学，如果患者能弄懂这一点，对手术后如何去生活，是非常有帮助的。”\n</p>', '腾讯健康频道,专业健康资讯门户网站，提供实用,全面的健康信息,包括中医,保健,饮食', '腾讯健康', 'admin', '0', '1', '2', '2016-07-15 17:46:52', '2016-07-21 17:02:55');
INSERT INTO `articles` VALUES ('50', '2', '冰激凌含糖量偏高当心冷饮“甜蜜陷阱', '炎炎夏日，不少人喜欢吃冰激凌、雪糕消暑，美味的冰激淋更是孩子们和女生们的最爱，同时，冰激凌又含有大量的糖分、奶油和脂肪，如一味贪图清凉而不控制摄入量，很容易掉进冷饮的“甜蜜陷阱”。', 'http://o9sccet84.bkt.clouddn.com/20160720142508290.jpg', 'http://o9sccet84.bkt.clouddn.com/20160719174131677.jpg', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>许多冰激凌含糖量超世卫“警戒线”</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	笔者随机对市场上20款冷饮包装上标称的碳水化合物含量统计发现，其平均含糖量为22.6%。其中，和路雪可爱多草莓口味冰激凌的含糖比例最高，为37.8%；蒙牛冰+蓝莓香草口味冰棒的含糖比例最低，为13.5%。不过，以产品的含糖总量计算，一份275克重的和路雪香草口味冰激凌含糖量高达65克，比一瓶500毫升的可口可乐含糖量还要高出12克。此外，蒙牛蒂兰圣雪香草口味冰激凌（245g）、雀巢巧克力味冰激凌（251g）含糖量也不低，分别为44克和49克。和路雪可爱多草莓口味冰激凌（67g）含糖量为25.3克。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	根据世界卫生组织的建议量，成年人每天摄入的糖不应超过25克。按此计算，上述4款冰激凌均超过了世界卫生组织推荐的每日糖摄入量。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	250g冰激凌含糖量相当一天饭食。通过对市面上20款冷饮的含糖量对比发现，奶油、巧克力口味的冰激凌含糖比例相对较高，大多数产品含糖区间在23%-38%；提子、豆类雪糕含糖比例次之，平均为21%；冰棍、冰棒含糖量最低，含糖比例通常不超过20%。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	中国营养学会会员郑林表示，人体每天通过谷物、粮食获取的糖分约为50克。按此计算，每食用一份250克装的大份冰激凌，就相当于摄入了接近人体一天吃饭获得的糖分。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	散装冰激凌含糖量不“明示”。预包装冷饮的含糖量尚且可从其“营养成分表”中读取，但类似于DQ、哈根达斯等散装冷饮的含糖量则往往无从查询。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	笔者近日走访北京部分DQ门店发现，其出售的散装冰激凌蛋糕包装上仅有配料表，没有营养成分表，现场店员也不知道产品的具体含糖量。哈根达斯散装冰激凌也同样存在类似问题，不过其外带包装上标注了净重、碳水化合物、脂肪等数值。哈根达斯官方客服表示，其门店所售散装冰激凌的含糖量与外带杯装冰激凌上的标签一致，“所有冰激凌都是含糖的，没有低糖或无糖产品”。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	针对散装冷饮的含糖量，台湾《健康》杂志曾在2014年随机选取了麦当劳、7-11、汉堡王、宜家家居等9家连锁商店销售的甜筒进行检测，发现多款散装冰激凌含糖量较高。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>专家建议应控制每日冷饮摄入量</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	中国农业大学食品科学与营养工程学院副教授朱毅介绍，通常来说碳水化合物的量约等于含糖量。如果糖分摄入过多，人体心脏负荷会过重，甚至引发甜食综合征，造成人体免疫能力下降，代谢功能紊乱。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	食品与营养信息交流中心专家阮光锋表示，部分冷饮含糖量相对较高，食用过多容易对健康造成不利影响，但偶尔食用并无大碍。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	中国农业大学食品科学与营养工程学院副教授范志红则建议，吃冰激凌的频次要拉大，最好购买小包装，食用冰激凌后要少吃其他甜点，“三高人群”同时还要减少肉奶等食物的摄入量。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	味道浓厚的牛奶冰激凌钙吸收率更高。冰激凌由于类型不同，乳脂肪含量也不同，比起那些乳脂肪含量较高、味道浓厚、可以充分享受其香甜的香草、牛奶等口味的冰激凌，水果味的冰激凌热量相对来说比较低。只是，果子露冰激凌热量低是因为水分较高，从糖分来看，反而比味道浓厚的冰激凌更多。而且，若是制作中添加牛奶的冰激凌，钙的吸收率可达40%。所以可以偶尔享受一下那些味道浓厚的冰激凌。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	下午15时至18时吃冰激凌最佳。下午15时至18时，是一天中体温最高、最难发胖的时间段，这时可以适当吃些冰激凌。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	享用冰激凌之后不妨喝杯暖茶。食用冰激凌后，内脏都似乎一下子冷下来了，消化器官机能也处在了低下状态。其实，热茶比冷饮更消暑。冷饮给人们带来冰爽体验的同时，也会给胃肠、咽喉带来刺激，过量食用还可能导致胃痛、腹泻或其他不适。泡杯热茶，既能促使毛孔张开，促进汗液的分泌，还可以利尿，有助于降温，我国古时就有“热茶最解暑”的说法。英国科学家研究表明，热茶的降温能力大大超过冷饮制品，是消暑饮品中的佼佼者。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	控制冰淇凌食入量记住3个诀窍： 1.在空腹的状态下，糖分吸收会变得更容易，这时候吃高热量高糖的食品相当“危险”。所以，尽量在饭后吃雪糕，吃一点点就可以满足了。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	2.选择雪糕的品种时，要注意包装上的成分，优先选择卡路里含量较低的。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	另外，还可按照刨冰、冰棍、固体雪糕的顺序来选择，它们所含的卡路里、糖分以及脂质是次第增加的。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	3.冰激凌一天最多吃一个。当天从雪糕中摄入的卡路里最好控制在200kcal以内。\n</p>', '冰激凌含糖量偏高当心冷饮“甜蜜陷阱1111', '家庭医生在线', 'admin', '0', '1', '2', '2016-07-19 17:41:37', '2016-07-21 16:56:00');
INSERT INTO `articles` VALUES ('5', '16', '北大吴阶平泌尿外科医学中心首开3D打印肾脏先河', '两年前，美国维克·弗里斯特再生医学研究所所长、外科医生安东尼·阿塔拉在TED大会演讲时,当众捧出了一个3D打印而成的肾脏!当时很多人都在想，什么时候中国也能拥有这样的技术。目前，北京大学吴阶平泌尿外科医学中心圆了这个梦想，该医学中心已利用3D技术打印出了十余个和真的肾脏一模一样的肾脏原型。', 'http://o9sccet84.bkt.clouddn.com/20160715174934941.jpg', '', '<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	“这个就是长了肿瘤的肾脏。”医学中心副主任张弋教授拿起一个肾脏原型告诉记者，“肿瘤旁边这个是肾静脉，静脉壁很薄，手术时特别容易碰到，因此，这将是一台非常难做的手术。”\n</p>\n<br />\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	然而，利用计算机断层扫描（CT）打印出病人肾脏的3D原型后，医生能够清楚地看到病人肾脏上的血管位置，肿瘤位置，在手术实施前用这个肾脏原型进行演练，可以大幅提高手术的成功率。\n</p>\n<br />\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	张弋指出：“每个人的肾脏都是不同的，不仅大小不一样，在人体内所处的位置也千差万别。根据不同的肾脏原型，我们就可以有针对性地进行不同的术前准备，对手术的设计是非常有帮助的。”\n</p>\n<br />\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	此前，外科医生在进行手术前，摆在眼前的资料只有病人拍的各种片子，看了这些二维的片子后，医生再在脑海中构筑、重建，然后生成3D的立体的影像，最后再制定手术方案。\n</p>\n<br />\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	“这一过程需要长期的实践，你才会在手术中迅速找到病变的部位并准确切除。”当了二十多年外科医生的张弋教授并不讳言，这个过程需要十几年甚至更长的时间。\n</p>\n<br />\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	现在有了3D打印的肾脏原型，情况就完全不一样了。“不仅可以使外科医生对病人的肾脏和肿瘤有直观的了解，在实际手术中能非常精准地在一个较小的区域操作，在医患沟通方面也能大显身手。”张弋说。\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	如何与患者沟通是每个临床医生都要面对的难题。由于患者不具备相应的医学常识，当医生向患者讲解病情时，患者往往不得要领，不知所云。张弋指出，“临床中存在太多无效沟通，它不但造成医患关系的疏离，也增加了医患纠纷的可能性。”\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	有了3D打印技术，这种困扰医患双方的难题就得到了有效的解决。由于打印出来的是患者“自己”的器官模型，医生面对模型跟患者讲解时，就能有的放矢。而患者对于自身病情，手术过程，以及术后效果等等，会因此有一个直观的了解，减少对手术的恐慌，主动配合医生进行术前术后的治疗。\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	“把打印出来的原型用于医学培训，不仅有助于年轻的医生获取知识，也可以让枯燥且无新意的教科书重获新生。”张弋强调，除了帮助外科医生设计手术和进行有效的医患沟通外，打印出的原型在进行医生培训时，也是大有作为的。\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	“我们最初用3D打印肾脏时遇到了各种挫折。”由于在国内没有先例可以借鉴，张弋坦言他们的尝试并非一帆风顺，而是困难重重，比如寻找拥有相关资质和技术的厂家，比如资金等等，摆在他们面前的困难一个接着一个，但当打印出的原型一个个摆在他的面前时，他非常庆幸当初接下了医学中心那彦群主任给他的这个挑战。\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	对于两年前安东尼·阿塔拉捧出的那个3D打印的粉红色肾脏，张弋至今记忆犹新，当时就有人说这是一个可以移植的肾脏。张弋找到医学中心的那彦群主任告知他这一消息时，那主任笑着说：“这个当然是个讹传，但这无疑是一个寄托了美好希望的讹传。我们一直走在探索如何终结器官捐赠的路上，3D器官打印无疑是一个很好的尝试，我期待这个寄托了美好希望的讹传成为现实。”\n</p>\n<p style=\"font-size:14px;text-indent:2em;color:#595757;font-family:Simsun;background-color:#FFFFFF;\">\n	今天，张弋知道，他们向那老描绘的“现实”前进了一大步。\n</p>', '腾讯健康频道,专业健康资讯门户网站，提供实用,全面的健康信息,包括中医,保健', '腾讯健康', 'admin', '0', '1', '2', '2016-07-15 17:49:37', '2016-07-21 17:02:49');
INSERT INTO `articles` VALUES ('6', '15', '掉头发很厉害怎么办，防治脱发方法很重要', '随着脱发的人群不断增加，不少人青年男女毅然开始担心自己是否会脱发，毕竟头发是一个人外在形象的重要体现,而外在形象又直接影响别人对自己的第一印象。可脱发却让外在形象大大折扣，这确实是令人非常担心的一件事情。担心自己会不会脱发以及担心如何防治脱发?', 'http://o9sccet84.bkt.clouddn.com/20160715175219892.png', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	朋友们，倘若刚开始掉发时，你不在意，不正常掉发时，你依然不在意，大量掉发即脱发的时候你装作没看见，那么我可以负责任的告诉你，此时你的发根已大部分将进入休止期，原本一个发根可以长处4-5根头发，此时已锐减到一个发根只有一根，甚至没有头发了。任这种情况继续下去，估计你会“中奖”，正式踏入“高额头”“聪明绝顶的行列”，真正到了这个时候，你可以试想下自己外在形象是什么样的。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	众所周知，所谓脱发，其实就是大量的掉头发，护理不及时导致的头顶稀疏，甚至可以轻松看到头皮，更严重的就会出现“发际线升高”“秃顶现象”。所以朋友们应在头发刚开始不正常掉落的时候就开始护理,避免脱发的发生。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	再者，现在的年轻人不仅上班需要面对电脑，下班了还要玩几把LOL，因为英雄联盟似乎已经成为生活的一部分。有这些经历的朋友们应该深有体会，不仅脸上满是油，就连用手摸下头发，都会明显感觉满手油腻，时间久了，头发就会大把大把的脱落，这也是现在脱发现象越来越年轻化的一大原因。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n		如何防治脱发，就用黑乐雅!朋友们也许会问，黑乐雅真的能防治脱发吗?答案是“Yes”、“Yes”、“Yes”，重要的事情说3遍。黑乐雅拥有使用方便，健康安全，高效不复发等特点，经过千万消费者亲身体验和认可的治疗脱发的有效产品，其特有的有效活性成分能起到扩张头皮毛细血管、刺激毛囊、改善头发生长环境、起到营养发根的作用，有效预防头发的脱落，并促进头发的生长。\n	</p>\n	<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n		另外，为了更好的防治脱发，实现止脱生发，日常生活习惯也是很重要的。俗话说“民以食为天”，吃也是就要讲究方式方法的，一定要均衡营养，保证身体的正常营养供给，为头发的生长提供必备的蛋白质、矿物质以及微量元素等。\n	</p>\n	<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n		平时繁忙之余，应多出去走在，适当的锻炼有助于身心健康，延年益寿，对于防治脱发自然好处多多。“生命在于运动”，这句话一直以来在人们的大脑中流传，但是真正做到的又有多少。朋友们，为了健康，为了个人形象，为了乌黑亮丽的秀发，让我们动起来吧。\n	</p>\n</p>', '掉头发很厉害怎么办', '', 'admin', '0', '1', '2', '2016-07-15 17:52:36', '2016-07-21 17:02:44');
INSERT INTO `articles` VALUES ('7', '15', '面部毛囊炎能治好吗 教你这几招方法就够', '在这个炎热的季节，可能很多人都热的一直处于面部出油出汗状态，脸上有毛囊炎症状的帅哥和美女们可能更加烦恼，为什么呢?夏季面部毛囊炎的症状真心不好受，尤其是冒痘痘，出现白头和脓包的你，对于夏日的热情也是充满着无奈与感叹。今天就给大家教几招夏日祛除毛囊炎的方法。', 'http://o9sccet84.bkt.clouddn.com/20160715175335214.png', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	记者采访皮肤科医生了解道：导致面部毛囊炎的病原菌主要还是金黄色葡萄球菌，偶尔表皮葡萄球菌、链球菌、假单孢菌和类大肠杆菌。毛发的牵拉、摩擦、搔抓引起的损伤，皮肤的浸渍，局部密封包扎及应用皮质激素等是导致面部毛囊炎的诱因。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	其实很多面部毛囊炎的患者大多是年轻的女性居多，也是女性朋友皮肤上经常会出现的疾病，所以，女性朋友对这种疾病应该是比较熟悉的。然而，很多患者在患上毛囊炎疾病之后，却一直不去医院治疗，所以让自己错过了治疗疾病的最佳时期，所以得了毛囊炎及时医治还是很重要的，建议使用肤平康乐是必不可少的，而且效果比较好。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>1、面部毛囊炎能治好吗之全身疗法</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	当患者皮损比较严重，同时，自己毛囊炎疾病还出现了黄色脓液的症状时，建议患者可以采取这种全身治疗的方法，应用抗生素的药物来治疗，从而控制住自己面部毛囊炎疾病的病情，也能够防止自己的肌肤受到更深层的伤害。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>2、面部毛囊炎能治好吗之免疫疗法</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	对于面部毛囊炎疾病反复发作的患者来说，我们则可以使用免疫的生成疗法，来起到防止疾病反复发作的治疗效果，同时，对于病情比较严重的患者来说，免疫疗法也是具有比较好的治愈效果的，。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>3、面部毛囊炎能治好吗之局部疗法</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	在使用局部疗法治疗毛囊炎疾病的时候，患者一定不能够用水清洗自己的患处，我们也要避免自己用手去抓挠患处，在皮损部位比较严重的地方，可以涂抹具有治疗效果药物比如肤平康乐，防止自己的患处出现炎症的疾病反应。一般患者会选择中药制的肤平康乐软膏在患处进行涂抹。草本中药制剂，可以直接渗透皮肤受损部位，连续使用20天以上即可看到明显的效果。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	对于以上几种祛除毛囊炎的方法，建议选择外用药物治疗;而毛囊炎属于慢性病，治疗需要外用加内服的方法来综合性的来进行治疗，建议先服用一个星期的罗红霉素(此药属于抗生素，建议在药师的指导下服用)，外用，完后外用肤平康乐来进行涂抹治疗，记得罗红霉素服用一个星期就停用，之后改为服用转移因子治疗。基本上一个月就可以彻底的康复。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	以上给大家介绍的，就是面部毛囊炎。希望所有的男性和女性在患了面部毛囊炎之后，其实还要做到饮食忌口，这样才能更有利于治疗，再合理运用正规的肤平康乐软膏治疗，根据自己的病情，然后适量涂抹，这样才能够治疗好面部毛囊炎的疾病，远离疾病的困扰。\n</p>', '面部毛囊炎能治好吗', '', 'admin', '0', '1', '2', '2016-07-15 17:53:38', '2016-07-21 17:02:37');
INSERT INTO `articles` VALUES ('8', '15', '面部脂溢性皮炎发红出油又痒又痛怎么办', '很多人突然觉得脸上突然开始出油变多，发红、还有了莫名的小疹子，去医院检查竟然是脂溢性皮炎。脂溢性皮炎大多出现在面部和头部，常见就是面部发红，有小红疹，出现脱皮，出油等现象，脂溢性皮炎到底是什么?外用肤脂平需要多久才能治好呢?今天就来为大家来揭开脂溢性皮炎的真正面目。', 'http://o9sccet84.bkt.clouddn.com/20160715175513560.png', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	脂溢性皮炎主要出现于成人以及新生儿皮脂腺比较丰富的部位，比如头部哇面部哇，有的严重一点可能会在背部出现，主要症状就是皮肤泛红、出现小疹子，面部出油增多，有的会出现片状红疹，确实是很影响形象的一种疾病。治疗上常外用肤脂平涂抹治疗，发展到后期会出现皮痂皮屑，堆积在患处可能还会出现异味。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>因为脂溢性皮炎的症状很容易和毛囊炎混淆，如果长时间不治疗会导致什么样的后果呢?</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	1.常感到瘙痒难忍，特别是在夜间睡觉的时候，因此，可导致睡眠质量差，精神状态差，抵抗力降低，从而加重病情，产生恶性循环。有可能会导致抑郁，自卑等一些不良心理的出现。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	2.长时间不治疗或者没有得到有效的治疗，可扩散到头皮等有毛发的部位，引起头皮脂溢性皮炎，头皮脂溢性皮炎的部位细菌大量滋生，很容易导致头皮毛囊破坏，引起头发脱落，断裂等毛发症状，如果没有及时的控制治疗，毛囊严重破坏，可使毛发不能正常生长，最终可能导致斑秃或者脱发。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	看到上面的危害，是不是感到一丝丝可怕，其实脂溢性皮炎治疗上也是很简单的，最重要的是用对药物。每天外用涂抹兰雪芙霖肤脂平，一天两次就行了，坚持必会有奇效，另外也可以去找老中医开点调节内分泌的中药搭配来喝，效果会更棒的。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>哪些原因引起脂溢性皮炎的呢?</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	1、夏季撸串烧烤、饮食太过肥甘油腻，食糖、脂肪过多、饮酒、过食辛辣油腻的食物，内分泌功能失调、消化功能失常。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	2、代谢障碍、遗传因素、维生素B族缺乏以及物理、化学刺激，很是经常搔抓或用碱性洗涤用品等均可加重脂溢性皮炎的发作。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	3、精神紧张，过度劳累，细菌感染。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	4、与性腺分泌紊乱有关，为雄激素分泌亢进所致。有的以为有可能与免疫、遗传、激素、神经和环境因素有关。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	治疗上都很简单的，接下来就是不简单的生活注意事项了。在用肤脂平治疗的时候你就会和烤肉啤酒火锅海鲜这些美味们说再见了，每天老老实实吃点清淡自然的食物就行了，烟酒什么的都不要想了，是不是觉得有一丝丝困难?坚持下来，脂溢性皮炎就会离你而去，想想这个是不是就有了坚持的动力。祝愿每个得脂溢性皮炎的孩子早日恢复美丽!\n</p>', '面部脂溢性皮炎', '', 'admin', '0', '1', '2', '2016-07-15 17:55:18', '2016-07-21 17:02:31');
INSERT INTO `articles` VALUES ('9', '14', '狐臭的最佳治疗方法 你知道是什么吗？', '人们之所以会患上狐臭，主要是人体腋下存在的大汗腺受到了细菌的破坏所致，当大汗腺被臭分子菌群感染，那么原本应该生态洁净的腋下环境就会受到严重的污染。在这种环境下分泌出来的汗液，会呈现出发黄、异常粘腻以及发臭等现象。狐臭汗液散发的臭味，特别像是狐狸身上的味道，所以被人们称为狐臭。', 'http://o9sccet84.bkt.clouddn.com/20160718151806180.jpg', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	狐臭让男人失去了自信，让女人失去了美丽!狐臭患者还时常遭到别人的白眼和歧视。无论是在工作或生活中、还是在婚姻上都屡受打击，承受着常人无法承受的心理压力和内心煎熬，面对狐臭他们束手无策，狐臭最佳治疗方法是什么呢?下面介绍几个治疗狐臭疗效较好的几种方法。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	1、中药治疗腋臭\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	中药除腋臭，是在中医基本理论的指导下，以清热利湿、通血活络、芳香避秽为治疗原则，通过内服及外用中药的方式，内可调节内分泌、敛汗解毒，外可防腐、燥湿、杀菌、香肌，标本兼治，共同达到去秽除臭的目的。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	另外，中医推荐狐臭患者可使用中药草本植物配方的帕芙欧香体露来治愈狐臭，按疗程使用效果显著。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	2、注射法腋臭去除术\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	无水乙醇注射法腋臭去除术是采用无水乙醇作为注射材料，利用它吸收组织中的水分，导致局部产生无菌性炎症、纤维化、坏死，导致顶泌汗腺萎缩和导管闭塞，最终组织被吸收，顶泌汗腺被破坏，但是注射的无水乙醇除了对顶泌汗腺造成破坏以外也会对正常组织造成一定程度的损伤。该方法一般需要分两次进行才能达到较理想的效果，且术后维持时间相对较短。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	3、物理法腋臭去除术\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	微波法腋臭去除术是利用微波的高频电磁波，通过治疗头接触组织，使组织中的水分子在电磁场中相互摩擦产热，短时间内达到100℃并扩散，使局部组织的蛋白质快速凝固、坏死，最终丧失排汗功能，从而达到治疗目的。该方法适合于轻度腋臭者，但治疗效果不彻底，易反复。\n</p>', '狐臭的最佳治疗方法', '', 'admin', '0', '1', '2', '2016-07-15 17:57:53', '2016-07-21 17:02:25');
INSERT INTO `articles` VALUES ('10', '14', '怎样正确有效地控制住脚臭？你知道吗？', '既然细菌和脚汗是脚臭的「源头」，那么控制细菌繁殖和保持足部干爽就是防治脚臭的「着力点」。所以，要控制脚臭，你还需要结合其他办法多管齐下。国内外临床医师总结了一些防治脚臭的办法，主要包括以下几点：', 'http://o9sccet84.bkt.clouddn.com/20160718151842413.jpg', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>脚部清洁</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	1.每日至少一次，用肥皂和温水洗脚或泡脚5–10分钟以上，注意清除脚趾缝、足底等多汗部位的污垢和死皮；\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	2.定期修剪脚趾甲，减少污垢、细菌的藏身之处；\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	3.没有消毒药品时，可以往洗脚水里撒一点发面用的小苏打；\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	4.洗脚沐浴后要将双脚彻底擦干，然后在鞋子里放上一些除臭的脚臭粉如帕芙欧脚臭粉，再穿鞋袜，那么脚就不臭了；\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	5.情况允许的条件下，应尽量将双脚从鞋袜的束缚中解脱出来，当然不建议在公众场合随意脱鞋。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>针对鞋袜</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	1.由于附着脚汗和脱落皮屑的鞋袜有大量细菌孳生，因此袜子要每日换洗，鞋子也要更换使用；\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	2.换下的袜子要翻过来洗，以清除内部的皮屑和细菌，并且要和其他衣物分开清洗；\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	3.鞋子和鞋垫可以使用抑菌喷剂或散剂进行消毒，也可以放到太阳底下靠紫外线杀菌；\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	4.没有消毒药品时，可以往鞋袜里撒一点发面用的小苏打，碱性环境可抑制细菌的生长，分解产生的二氧化碳也不会残留有特殊气味。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	5.选择透气性好的鞋袜，比如纯棉、羊毛袜，真皮鞋，网格运动鞋等。尼龙丝袜，光脚穿鞋（拖、凉鞋除外）等会增加脚汗，应避免；\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	6.剧烈运动，鞋袜打湿后最好及时更换；\n</p>', '怎样正确有效地控制住脚臭', '', 'admin', '0', '1', '2', '2016-07-15 17:59:01', '2016-07-21 17:02:19');
INSERT INTO `articles` VALUES ('11', '14', '治疗手上的湿疹 还是用这一招最管用！', '治疗手上的湿疹 还是用这一招最管用！', 'http://o9sccet84.bkt.clouddn.com/20160718151914767.jpg', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<br />\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<img src=\"/public/admin/kindeditor/attached/image/20160720/20160720150241_15921.png\" alt=\"\" />\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	好久都不洗手了，每天洗脸带个手套，我真是想哭呀……\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	慢性湿疹患者，双手、两颊长满，长年饱受病痛折磨\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	患病感受先讲一下：长满小颗粒，一挤流黄水，这不能吃，那不能洗，奇痒无比！\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	医生一直没有给我解释清楚湿疹的病因，几乎所有医生都说的过敏引起的，所以我去查了过敏原。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	两次试验，7种过敏原...\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	屋尘螨、粉尘螨、杂草、艾蒿、杨树种子那叫啥？、硫柳汞、秋兰姆混合物\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	后两种什么鬼？\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	从此以后，切断与过敏原接触的一切可能...\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	然后口服抗组胺药，手涂治疗湿疹的膏药，市面上很多，好多都含激素\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	用来用去，不但湿疹，完蛋玩意还得激素依赖症，最后朋友推荐用了抑制真菌生长的中药成分帕芙欧珍肤膏，才算治好了我手上的湿疹。\n</p>', '手掌，湿疹', '', 'admin', '0', '1', '2', '2016-07-15 18:00:11', '2016-07-21 17:02:14');
INSERT INTO `articles` VALUES ('12', '2', '21日至24日成都两个区域 单日单号行双日双号行', '7月23日至24日，2016年第三次G20财长和央行行长会（以下简称“G20成都会议”）将在成都举行。为确保G20峰会期间成都交通出行通畅，成都交警将在G20成都会议期间对中心城区部分道路实施交通管控。其中，人民南路、天府大道一线、世纪城路等道路将实施单双号限行举措。\n7月17日上午，成都交警对通告进行权威解读：此次施行的车辆限行方案，系在特定时间特定区域设点，不会成为常态化交管措施', 'http://o9sccet84.bkt.clouddn.com/20160720134311612.jpg', 'http://o9sccet84.bkt.clouddn.com/20160720132828225.png', '<div id=\"CD_F_BT_Width\" class=\"l_qq_com\" style=\"font-family:宋体, Arial, sans-serif;margin:0px auto;background-color:#FFFFFF;\">\n	<a class=\"absolute a_cover\" href=\"http://c.l.qq.com/lclick?oid=2797811&cid=1492489&loc=CD_F_BT_Width&click_data=dXNlcl9pbmZvPXV4M19uVFk2RmhlLUFQeUJQRGdKRUxzWS1aZz0=&soid=TCDR3joXV4wuhwV8kgYip432AZ18&chl=0&index=1&page_type=2&aver=0&dtype=0&k=21%E6%97%A5%E8%87%B324%E6%97%A5%E6%88%90%E9%83%BD%E4%B8%A4%E4%B8%AA%E5%8C%BA%E5%9F%9F%20%E5%8D%95%E6%97%A5%E5%8D%95%E5%8F%B7%E8%A1%8C%E5%8F%8C%E6%97%A5%E5%8F%8C%E5%8F%B7%E8%A1%8C&t=21%E6%97%A5%E8%87%B324%E6%97%A5%E6%88%90%E9%83%BD%E4%B8%A4%E4%B8%AA%E5%8C%BA%E5%9F%9F%20%E5%8D%95%E6%97%A5%E5%8D%95%E5%8F%B7%E8%A1%8C%E5%8F%8C%E6%97%A5%E5%8F%8C%E5%8F%B7%E8%A1%8C_&r=&s=\" target=\"_blank\"></a> \n	<div style=\"background:url(;\">\n	</div>\n</div>\n<p>\n	<p>\n		<br />\n	</p>\n	<h1 style=\"font-size:26px;font-weight:normal;font-family:微软雅黑;\">\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong><img src=\"/public/admin/kindeditor/attached/image/20160720/20160720134341_92735.png\" alt=\"\" /><br />\n</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong><br />\n</strong>\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong><br />\n</strong>\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>重要的事情再说一遍</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>成都交警(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/cdjiaojing#pref=qqcom.keyword\" target=\"_blank\">微博</a>)权威解读<a target=\"_blank\" href=\"http://finance.qq.com/zt/2009/G20pzb/index.htm\">G20</a>成都会议期间临时交管措施</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			7月23日至24日，2016年第三次G20财长和央行行长会（以下简称“G20成都会议”）将在成都举行。为确保<a target=\"_blank\" href=\"http://news.qq.com/zt/2009/sandafh/\">G20峰会</a>期间成都交通出行通畅，成都交警将在G20成都会议期间对中心城区部分道路实施交通管控。其中，人民南路、天府大道一线、世纪城路等道路将实施单双号限行举措。\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			7月17日上午，成都交警对通告进行权威解读：此次施行的车辆限行方案，系在特定时间特定区域设点，不会成为常态化交管措施。\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>限行范围</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>人民南路、天府大道一线；世纪城路</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			根据成都市交管局此前发布的通知，7月21日至24日，每天早上7点半至晚8点，在人民南路、天府大道一线（北起东御街路口，南至华府大道路口）以及世纪城路道路上行驶的汽车按车牌（含临时车牌）最后一位阿拉伯数字，实行单号单日、双号双日通行（单号为1、3、5、7、9，双号为2、4、6、8、0）。7月21日至7月22日，二、三环路之间区域汽车尾号限行交通管理措施维持不变。\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>市民疑问</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>限行会否长期实施？交警解释：仅施行4天</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			该通知发布后，引起许多市民担心，“会不会以后都这样限行？”对此，成都市交管局科规处优化科科长周伟潮解释道，此次单双号限行，系非常态化交管措施，是在特定时间特定区域内设定，与现行的二三环间尾号限行举措有着一定区别，“大家不用太担心。”\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			这次在特定时间、特定区域内的限行方案，时间短、范围小，对交通的影响不大。“仅在21日到24日的4天内施行，并且夜间8点后一直到清晨7点30分，机动车辆都不受限。”周伟超说，单双号限行只在会议涉及最紧密的两条道路，即人南一线到天府大道这条城市中轴线和世纪城路实施，其他区域不受影响。\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>道路限行回家会不会绕远？交警解释：可横向穿越</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			对于有些市民担心的“回家绕远路”问题，周伟潮说，周边居民出行回家并不会受到太大影响。“人南到天府大道这条中轴线虽实施单双号限行，但车辆横向穿越这条线则不受影响。”周伟超举了个例子，即使当天限行，车辆仍然可通过府城大道下穿、一环路跳伞塔下穿等横向道路通过人南、天府大道中轴线，“而且人南、天府大道中轴线东西两侧有多条平行道路可供绕行，包括红星路南延线、益州大道、剑南大道等道路，通行条件都比较好。”\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>成都市民请注意：21日-24日两区域单双号限行</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			21日：单号通行22日：双号通行23日：单号通行24日：双号通行(7月21日至7月22日，二、三环路之间区域汽车尾号限行交通管理措施维持不变)。\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>特别提醒</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>21、22日，人民南路四段两种限行并存</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			“人南、天府大道中轴线单双号限行，系全路段限行，包括主道、辅道。”周伟潮提醒市民，在人民南路四段单双号限行路段上，与二三环区间尾号限行区域所重叠，“单双号限行与尾号限行并存，两个交管措施驾驶员都要遵守。”\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			7月23日、7月24日分别为周六、周日，成都二三环区间不实施尾号限行，将完全按照单双号限行举措实施。\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>这些区域临时管控，无证不要前往</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			2016年7月21日至7月24日每天7：00至24：00，以世纪城娇子会议中心为中心，北至世纪城北门，南至假日酒店西楼，东至红星路南延线与世纪城路交叉路口，西至广电路口以内的区域，除持有公安机关核发G20成都会议专用车证的车辆外，禁止其他车辆通行。\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			7 月 24 日下午 1 点至晚 10点，锦官驿街东口至西口，除持有公安机关核发G20成都会议专用车证的车辆外，禁止其他车辆通行；滨江东路、滨江中路、滨江西路、城守东大街、上东大街、下东大街、芷泉街、紫东楼街、红星路四段、天仙桥南路等道路，将根据实际需要，采取临时限制交通的措施。社会车辆可选择周边道路绕行。上述道路沿线单位及个人的机动车辆凭驾驶人的有效居住证明或车辆的登记证明通行。“当天需前往上述地区办事的市民，最好选择其他交通方式。”成都市交管局勤务处民警薛继峰建议通过性车辆，选择一环路、人民南路等周边道路绕行。\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			<strong>会议期间相关区域，可免费坐公交</strong> \n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			G20成都会议期间，公交集团将在临时交通管理的沿线调动公交车辆、增加运力、加密班次；成都市交委(<a class=\"a-tips-Article-QQ\" href=\"http://e.t.qq.com/cdjw2013#pref=qqcom.keyword\" target=\"_blank\">微博</a>)、公交集团也出台政策，会议期间的临时交通管理措施限行区域，将实行免费乘坐公交车的措施。对途经人民南路、天府大道及浆洗街、红星路沿线的公交线路，在2016年7月21日至24日期间，刷卡免费乘坐公交车。\n		</p>\n		<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n			成都交警提醒市民，在会议期间尽量选择公交、地铁等公共交通绿色出行。华西都市报记者杨雪实习生何欣欣\n		</p>\n<br />\n	</h1>\n	<p>\n		<br />\n	</p>\n</p>', '成都，单日单号行双日双号行', '', 'admin', '0', '1', '2', '2016-07-18 09:23:23', '2016-07-21 17:02:09');
INSERT INTO `articles` VALUES ('15', '3', '反复鼻出血两年 原来是“熊孩子”不良习惯惹得祸', '你小时候流过鼻血吗？想来大多数人都会回答“流过”，甚至有人会说：流鼻血太正常了。但是四川万源有这样一个“熊孩子”小川，他的情况却很特别。', 'http://o9sccet84.bkt.clouddn.com/20160718093347252.png', '', '<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>垃圾食品吃太多 小川养成抠鼻习惯</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	小川（化名）今年11岁，家住达州(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/weidazhou2011#pref=qqcom.keyword\" target=\"_blank\">微博</a>)市万源县，现在正读5年级。据他妈妈反映，小川从小就很喜欢吃零食，其中辣条和方便面是他的最爱，他上学的零花钱几乎都奉献给了这些垃圾食品，且每次吃完零食就会感觉鼻子不舒服，为此他总是喜欢用手指去抠，久而久之他就养成了抠鼻子的习惯。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>常年抠鼻 导致鼻腔糜烂</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	掏鼻子的习惯其实并没有引起父母的注意，引起父母注意的是他流鼻血这一现象。起初爸妈对流鼻血不以为然，认为这是正常的，可是时间长了他流鼻血的次数越来越频繁，他们这才带着孩子去当地的门诊治疗，但结果并不理想，孩子的病情依旧反复发作。妈妈怀疑这和他吃垃圾食品有关，如果不控制小川吃零食的数量，他每天睡觉前都很可能流鼻血。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>千里寻医 只为儿健康</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	在上课的时候，小川也会时常流鼻血，为此老师还多次给小川父母打电话，希望他们能好好带小川治疗，以免影响学业和正常的上课秩序。另外，有一次小川的妈妈醒来，发现还在睡觉的小川满脸是血、枕头和被子上也染红了一片，这才意识到问题严重性。暑期来了，小川爸妈觉得孩子的病情不能再拖了，于是痛下决心要把孩子的病治好。小川爸爸在网上了解到成都仁品耳鼻喉专科医院正在进行“2016畅呼吸青少年耳鼻喉健康公益援助活动”，立马向公司请假，奔赴千里来成都为孩子治疗。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>常年流鼻血竟是“抠鼻”惹的祸</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	成都仁品耳鼻喉专科医院专家张治军为小川进行了详细的检查，发现小川患有肥厚性鼻炎，且常年抠鼻已经造成了鼻腔糜烂、鼻部血管扩张，所以会经常出现流鼻血的现象。张医生称，如果小川的症状还不加以治疗，常年下去会导致出血量加多和病情反复，自愈的可能性非常小。为此，仁品医院对小川采用了鼻出血等离子微创手术治疗，很快，小川不再流鼻出血了。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>关注青少年耳鼻喉健康 需要全社会共同努力</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	采访中，成都仁品耳鼻喉专科医院副院长李小明告诉笔者，小川的情况并不是个案，目前我国青少年耳鼻喉疾病在学生群体中发病率已高达35%，严重影响孩子们的学习和身体发育；为了让更多青少年耳鼻喉患者在2016年暑期得到有效诊疗，响应成都电视台和四川省扶贫基金会的倡议，成都仁品耳鼻喉专科医院在暑期发起了“2016畅呼吸青少年耳鼻喉健康计划”，活动期间（2016年7月1日-8月31日），凡四川省内的青少年耳鼻喉患者在仁品耳鼻喉专科医院就诊检查费减半，家庭贫困的青少年需手术患者还可申领四川省扶贫基金会提供的1000元助学金援助。凡18岁以下的青少年耳鼻喉疾病患者，均可到成都电视台3套热线188栏目预约报名\n</p>', '反复鼻血，不良习惯', '', 'admin', '0', '1', '2', '2016-07-18 09:33:51', '2016-07-21 17:00:23');
INSERT INTO `articles` VALUES ('13', '2', '成都桂溪公园部分试开放 9个人民公园那么大', '被誉为成都版“纽约中央公园”的桂溪生态公园将于今日完成景观施工并部分对外试开放。拥有市区内稀缺的大面积可进入式草坪与林荫绿道慢跑体系，桂溪生态公园将有效降低大源CBD区域的热岛效应，市民可以在大草坪、林荫道上肆意享受阳光、微风。', 'http://o9sccet84.bkt.clouddn.com/20160720134503957.jpg', '', '<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	被誉为成都版“纽约中央公园”的桂溪生态公园将于今日完成景观施工并部分对外试开放。拥有市区内稀缺的大面积可进入式草坪与林荫绿道慢跑体系，桂溪生态公园将有效降低大源CBD区域的热岛效应，市民可以在大草坪、林荫道上肆意享受阳光、微风。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	昨日，2016年十大民生实事半年集中检查第3组来到桂溪生态公园，探访项目建设情况。记者了解到，目前项目九成以上的建设已完成，正在进行房建和绿化收尾工作。而试开放的将是全部完工的区域。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>树木防尘治霾 路面还能透水</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	记者了解到，桂溪生态公园位于成都高新区(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/cdhtxc#pref=qqcom.keyword\" target=\"_blank\">微博</a>)天府绕城高速(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/cdrcgs#pref=qqcom.keyword\" target=\"_blank\">微博</a>)以南、天府一街以北、红星路南延线以西、益州大道以东，规划面积1400余亩，相当于9个人民公园。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	“绿地将留有大面积草皮，人游其间，视野开阔，还将安装体育、休闲设施。”成都市高新区(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/cdhtxc#pref=qqcom.keyword\" target=\"_blank\">微博</a>)相关负责人介绍说，对树种的选择上，以本土树种为主，还对防尘治霾的作用进行了考虑，主要种植黄桷树、香樟、小叶榕和银杏等。除了绿树成荫，市民还可以在此欣赏到樱花和紫薇等花海。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	此外，桂溪生态公园以海绵城市的范例区来打造，路面不以水泥浇筑，而以天然石子和胶质粘合，透水率达70%。收集的雨水进行统一处理，用于全园浇灌和景观用水。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	桂溪生态公园开放后，必然会迎来巨大人流，对此，武侯区长寿苑社区居民范方凌建议，绿地上应多修建人行绿道。成都市第二十四幼儿园园长唐琳建议，除了草坪，公园可做空间的绿化和大型树木的移植，让绿意更丰富立体。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>迷你消防车可开到老小区楼下</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	老旧小区失火，消防车却开不进窄小通道，这种情况将在成都市区得到有效解决。昨日，第三组市民代表来到高新区双和社区微型消防站，这里最有效的配备当属迷你消防车，其体型轻便，配备水袋，能自如出入窄小通道。目前，迷你消防车已开进我市1425个社区，预计年内将覆盖1782个社区。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	第三组市民代表昨日还前往了大邑县三岔镇山墩村、安仁镇廖营桥、苏场镇大寨桥等点位，检查市发改委、市交委(<a class=\"a-tips-Article-QQ\" href=\"http://e.t.qq.com/cdjw2013#pref=qqcom.keyword\" target=\"_blank\">微博</a>)等部门落实新建粮食烘干中心和病害桥梁整治的情况。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	在座谈会上，各部门负责人总结，所有民生实事目标做到时间过半、任务过半。市民代表发言点赞，并针对参观点位的不足和从群众中搜集的意见，向职能部门积极建言献策。\n</p>', '成都，桂溪生态公园，大面积', '', 'admin', '0', '1', '2', '2016-07-18 09:28:41', '2016-07-21 17:02:04');
INSERT INTO `articles` VALUES ('14', '3', '做1事让癌症扩散快6倍', '压力对癌症患者的影响可能超乎以往想象。澳大利亚莫纳什大学最新研究发现，压力会让癌症扩散的速度增加六倍，就如同为癌症开辟了一条“高速路”。', 'http://o9sccet84.bkt.clouddn.com/20160718093036477.png', '', '<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	研究中，科学家对患有乳腺癌的小鼠进行实验，这些小鼠的肿瘤已经发生扩散，并研究压力信号对小鼠癌细胞扩散进程的影响，同时考察压力因素如何影响癌症治疗效果。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	研究参与者、该大学药剂学研究所肿瘤生物学家艾丽卡·斯隆指出，研究最关键的发现是压力能够加速肿瘤的扩散，承受压力的小鼠体内肿瘤的扩散速度比控制组小鼠快了6倍。压力就像为肿瘤开辟了一条高速路，让它们得以更快扩散。研究人员同时指出，压力也会妨碍癌症治疗的顺利进行。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	斯隆博士表示，癌症患者的压力，一方面来自患者自身的心理状态，另一方面，也来自身体承受的压力，比如患者接受的各种治疗。研究认为，化疗和手术，都会给身体和心理带来一定的损害。承认压力，并进行相应治疗，或许是更好的办法。目前该研究团队在进行相关药物的研制，以期阻断压力因素对癌症治疗的影响。\n</p>', '癌症患者，压力', '', 'admin', '0', '1', '2', '2016-07-18 09:31:05', '2016-07-21 17:00:30');
INSERT INTO `articles` VALUES ('16', '3', '干眼症高发 6招预防干眼症', '天气炎热，居然成为干眼症高发的季节。眼科医生认为，这与空调和电脑的长时间使用，以及佩戴隐形眼镜都有密切关系。干眼症治疗有哪些方法呢？进行相应生活上的调节多可明显改善症状，如果没有效果的还有多种医疗手段可以采用。', 'http://o9sccet84.bkt.clouddn.com/20160718093720333.jpg', '', '<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	天气炎热，居然成为干眼症高发的季节。眼科医生认为，这与空调和电脑的长时间使用，以及佩戴隐形眼镜都有密切关系。干眼症治疗有哪些方法呢？进行相应生活上的调节多可明显改善症状，如果没有效果的还有多种医疗手段可以采用。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>干眼症的治疗</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	拒绝干眼症需要保持良好的工作、生活习惯是预防干眼症的有效手段，可通过以下情况来防止眼泪的蒸发：\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>1．多眨眼。</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	眨眼是一种保护性神经反射动作，泪液层可以使泪水均匀地涂在角膜和结膜表面，以保持润湿而不干燥。一旦眨眼次数减少，直接导致泪水的量减少，而暴露在空气中的泪膜会快速蒸发，失去对眼球的保护力。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	操作电脑、驾车、读书等长时间用眼时，提醒自己不要太过专注，花些时间眨眼睛。一般大多数人五秒钟眨一次眼，每分钟眨眼约20次。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>2．注意用眼卫生。</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	要注意用眼卫生，勤洗手，不要用手揉搓眼睛，用眼一小时左右休息一会儿，闭目养神，眺望远处。对眼睑上有油性分泌物、碎屑、脱落物的患者来说，要注意保持眼睑卫生。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>3．长时间使用电脑者注意。</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	长期应用电脑工作的人群，特别是年轻人应该注意及时治疗干眼症症状，减少使用电脑时间，避免连续长时间使用电脑。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	工作的姿势和距离也是很重要的，尽量保持在60厘米上以距离，调整一个最适当的姿势，使得视线能保持向下约30度，这样的一个角度可以使颈部肌 肉放松，并且使眼球表面暴露于空气中的面积减到最低。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>4. 注意饮食</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	长期从事电脑操作者，要多吃一些新鲜的蔬菜和水果，同时增加维生素A、B1、C、E的摄入。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	为预防角膜干燥、眼干涩、视力下降、甚至出现夜盲等，应多吃富含维生素A的食物，如豆制品、鱼、牛奶、核桃、青菜、大白菜、空心菜、西红柿及新鲜水果等。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	维生素C可以有效地抑制细胞氧化。维生素E主要作用是：降低胆固醇，清除身体内垃圾，预防白内障。核桃和花生中含有丰富的维生素E。维生素B1可以营养神经，绿叶蔬菜里就含有大量的维生素B1。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	每天可适当饮绿茶、菊花茶，因为茶叶中的脂多糖，可以改善肌体造血功能，茶叶还有防辐射损害的功能。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	有些食品和饮料会使你的眼睛和身体脱水，巧克力、可乐。咖啡和茶都含有咖啡因，而咖啡因可消耗身体的水分，为了保持体内有充足的水分，我们要多饮水（如果可能的话一天至少要喝六杯水）。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>5．戴框架眼镜</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	隐型眼镜的确能很大程度地改善人的外貌，但它带来的痛苦也不算少，干眼症就是其中之一。长时间配戴隐型眼睛会使泪液分泌减少，因此戴隐型眼镜的人总会感觉眼睛干干的。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	预防干眼症，提倡多风的日子戴眼镜，游泳时也戴上护目镜，外出时戴上太阳镜。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>6．尽量少使用空调，少呆在湿度较低的房间里。</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	空调除了调节温度之外，还会抽湿，减少了空气里水分的含量。在这种干燥的环境中，泪膜蒸发率增加，容易使眼睛发干、发涩。气候干燥的冬季，为干眼症的高发季节。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	天气炎热，使用空调要注意定时开窗通风，或者在房间里放置一台空气加湿器。保持房间湿度在30%--50%之间。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	以上就是缓解眼睛干涩的干眼症治疗方法。另外，天气炎热又干燥，也别忘了要及时补水哦。\n</p>', '干眼症，6招预防，电脑', '', 'admin', '0', '1', '2', '2016-07-18 09:37:22', '2016-07-21 17:00:18');
INSERT INTO `articles` VALUES ('17', '4', '高考录取有6种状态 看清楚你是哪一种', '考生成绩达到某批次控制分数线并填报了相应批次志愿，那么该批次录取期间，考生就可根据省教育考试院公布的录取查询渠道，查询自己的录取状态信息。在这之中，考生查询到电子档案的状态可能有“自由可投”“已经投档”“院校在阅”“预退档”“预录取”“录取”等6种状态。这些状态分别代表什意思?考生又要注意哪些问题?7月13日，记者采访了省高考录取场相关负责人。', 'http://o9sccet84.bkt.clouddn.com/20160718094024666.jpg', '', '<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	考生成绩达到某批次控制分数线并填报了相应批次志愿，那么该批次录取期间，考生就可根据省教育考试院公布的录取查询渠道，查询自己的录取状态信息。在这之中，考生查询到电子档案的状态可能有“自由可投”“已经投档”“院校在阅”“预退档”“预录取”“录取”等6种状态。这些状态分别代表什意思?考生又要注意哪些问题?7月13日，记者采访了省高考录取场相关负责人。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	自由可投：表示考生成绩或其他方面没有达到投档要求，其电子档案没有投出去;或达到了投档要求，电子档案投出去后但不符合高校录取条件又被退档。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	处于“自由可投”状态表示考生未被该批次学校录取，考生要密切关注同批次的征集志愿时间并及时填报征集志愿，以免错过机会。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	已经投档：即考生成绩和其他方面达到高校投档要求，省高考录取场已将考生电子档案投给院校，但院校还没下载考生电子档案。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	处于“已经投档”状态表明考生电子档案已经投出，考生要注意查询档案所投学校。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	院校在阅：意味着院校下载了考生电子档案后正在审阅考生的档案。在该过程中，院校将根据招生章程公布的录取规则对进档考生进行专业安排，并确定“预录取”或“预退档”考生名单，但尚未确定正式录取与否。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	预退档：表示院校按学校录取规则录取时，对成绩偏低且不服从专业调剂或体检、单科成绩等原因不能录取的考生，做出“预退档”即准备退档处理，并已向省高考录取场提交“预退档”考生电子档案等待录检审核。对每一个“预退档”的考生，院校都会注明退档理由，经录取检查组审核同意方可退档。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	处于“院校在阅”和“预退档”状态时，考生一定要留意自己在高考报名时所留的通联通讯是否畅通，这过程中，如有需要，高校或省高考录取场可能会与考生联系，一旦错过机会，可能会给考生造成无法弥补的损失。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	预录取：即院校对符合录取条件的考生进行专业安排之后，上载了拟录取考生名单及录取专业，即为“预录取”状态。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	处于“预录取”状态并不表明正式被录取，须经省高考录取场录取检查组审核通过后方为正式录取。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	录取：对院校提交“预录取”考生，经省高考录取场录检审核，审核通过后即变为“录取”。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	处于“录取”状态的考生，恭喜你，已经被高校正式录取，这时就应注意查询自己的录取通知书了。\n</p>', '高考录取，批次志愿，录取查询渠道', '', 'admin', '0', '1', '2', '2016-07-18 09:40:28', '2016-07-21 17:00:12');
INSERT INTO `articles` VALUES ('18', '4', '全国22所名牌大学能低分录取 四川有两所', '“985工程”源自1998年5月4日江泽民在北京大学百年校庆上“建设世界一流大学”的讲话。最初入选“985工程”的高校只有九所，至2011年年末，共有39所高校位列其中。在此基础上，教育部又推出“211工程”，意即“面向21世纪、重点建设100所左右的高等学校和一批重点学科的建设工程”，共计112所高校。简而言之，“985”和“211”就是国家圈定的要重点扶持的高等院校。', 'http://o9sccet84.bkt.clouddn.com/20160718094215605.jpg', '', '<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	四川有五所211学校：电子科技大学，四川大学，西南交通大学，西南财经大学，四川农业大学。其中是国家985工程的学校有两所：电子科技大学和四川大学。“985”和“211”是中国优秀高校的代表，对于高校，这三个数字，意味着极大的政策、拨款倾斜；对于学生，这意味着自己考上名校的资格认定；对于一些企业，这是招聘启事中的第一轮筛选。但不是所有的学生都能考高分，顺利进入心仪的“985”和“211”，本文小编整理汇总了22所能够低分录取的名牌大学，四川地区有电子科技大学沙河校区和西南交通大学峨眉校区两所上榜，信息整理自网络，欢迎各位网友补充修正！师生家长一起来看看吧！\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	电子科技大学沙河校区是电子科大三个校区之一，位于位于成都市成华区(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/cdchenghua/#pref=qqcom.keyword\" target=\"_blank\">微博</a>)建设北路二段。占地800余亩，建有现代化的运动场、体育馆等体育设施。除了有信息与软件工程学院，还有微电子与固体电子学院，光电信息学院，物理电子学院，生命科学学院、国际教育学院等学院的部分本科生和研究生。信息与软件工程学院是电子科技大学25个普通学院之一，该学院成立于2001年，是教育部和国家计委联合批准成立的全国首批35所示范性软件学院之一。学院设有软件工程系、大型主机与网络安全工程系、信息工程系三个系。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	西南交通大学峨眉校区是西南交通大学“一校两地三校区”办学格局的重要组成部分，是中国近代交通工程、矿冶工程、土木工程高等教育的发源地。截至2016年2月，峨眉校区共有土木、机械、电气、交运等10余个教学系（部），26个本科专业（方向），5个研究生教育专业领域和研究方向（第一批研究生于2014年9月份入学报到），在校学生7400余人；校区建有物理、化学、力学、电工电子、机械、运输等6个国家（省）级实验教学示范分中心，铁路通信信号、城市轨道交通控制及通信平台等18个实验中心，高速铁路牵引供电综合实验系统、建筑材料、地下工程等16个实验室以及与国家铁路局、中国铁路总公司等单位共同建立的一大批覆盖校区各专业的实践基地。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>什么是“985”和“211”？</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	“985工程”源自1998年5月4日江泽民在北京大学百年校庆上“建设世界一流大学”的讲话。最初入选“985工程”的高校只有九所，至2011年年末，共有39所高校位列其中。在此基础上，教育部又推出“211工程”，意即“面向21世纪、重点建设100所左右的高等学校和一批重点学科的建设工程”，共计112所高校。简而言之，“985”和“211”就是国家圈定的要重点扶持的高等院校。\n</p>', '四川高校，名牌大学，低分录取', '', 'admin', '0', '1', '2', '2016-07-18 09:42:29', '2016-07-21 17:00:06');
INSERT INTO `articles` VALUES ('19', '4', '成都城区第四批普高录取未满额学校18日补录', '成都城区第四批普高录取未满额学校18日补录', 'http://o9sccet84.bkt.clouddn.com/20160718094513203.jpg', '', '<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<img src=\"/public/admin/kindeditor/attached/image/20160720/20160720150353_68548.jpg\" alt=\"\" />\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	<br />\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	成都市中心城区2016年第四批一般普通高中录取工作已于7月16日进行。根据 《成都市2016年高中阶段教育学校统一招生 （招收初中毕业生）考试工作实施规定》，本批次录取从高分段到低分段逐段投档，各分数段内志愿优先。投档中采取升学考试成绩（含加、降分，以下简称调档分）、综合素质评价等级相结合的办法，择优录取学生。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	普高录取投档比例为1：1，若调档分相同而超出调档比例的，按学生综合素质评价等级从高到低排列，若等级相同，则按学生语文、数学、英语三科升学分之和从高到低排列；若此分数也相同，则按语文、数学、英语、物理四科B卷成绩之和从高到低排列；若此分数也相同，则按体育成绩从高到低排列。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	本着既充分考虑考生成绩和志愿，又兼顾学校健康协调发展的原则，今年一般普通高中投档分数段确定为：570分及以上为一段，569到535以5分为一段。每一段投档时按志愿顺序依次投档。如果同一学校同一类计划（指统招或调剂，下同）有低分被录取而高分未被录取，一定是低分考生的志愿比高分考生的志愿顺序更靠前。若学生调档分达到学校的分数线，且填报了该校志愿而未被录取，则属于志愿顺序靠后或相同分段考生按录取规则排序时，小项排序靠后的情况。如果考生填报志愿时将某校的调剂志愿填报在靠前的志愿栏中，则可能出现考生成绩高于统招分数线而被调剂计划录取的情况。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	公办普通高中本批次只对调档分在535分及以上的考生进行投档、录取，所产生的分数线是指学校在本批次录取到的考生中最低调档分。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	7月16日晚8：00，第四批一般普通高中录取信息上网公布，考生凭中考网络应用通行证(身份证号和密码)登录中考网络应用服务平台，进入“录取结果查询”查询本人录取结果。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	据了解，还有少量普通高中学校统招计划未录满，已于7月17日8：00至14：00开通网上征集志愿系统，成都中心城区户籍考生调档分达到一般普通高中最低送档线、且未被录取的，凭中考网络应用通行证(身份证号和密码)登录中考网络应用服务平台，进入“补充征集志愿”进行填报，每位考生可补填统招生志愿1个。补录工作将于7月18日上午进行。\n</p>', '普高录取，成都城区', '', 'admin', '0', '1', '2', '2016-07-18 09:45:15', '2016-07-21 17:00:00');
INSERT INTO `articles` VALUES ('20', '10', '刘晓红 消化内科教授', '刘晓红，消化内科教授、主任医师，医学博士、理学博士，硕士导师，老年示范病房主任。', 'http://o9sccet84.bkt.clouddn.com/20160718135016108.jpg', 'http://o9sccet84.bkt.clouddn.com/20160720103250436.jpg', '<p>\n	<strong>刘晓红</strong>，消化内科教授、主任医师，医学博士、理学博士，硕士导师，老年示范病房主任。1982年毕业于北京医科大学医疗系，1982年毕业于北京医科大学，1990年北京医科大学第三医院消化科获医学博士学位，1995年日本九州大学医学部获医学理学博士学位（Ph.D）。2011年赴美国约翰霍普金斯老年医学科学习3个月。<br />\n从事消化内科临床工作近30年，老年医学6年。任老年医学科主任5年。曾先后任内科副主任、国际医疗部主任。有丰富的内科学知识、消化内科及消化内镜检查的临床经验；对疑难病例和老年患者尤为关注。发表文章120余篇。主编《老年医学速查手册》《老年医学诊疗常规/北京协和医院》，《国家执业药师考试指南-药学综合知识与技能（2015）》等。<br />\n任北京医师协会老年医学专家委员会主任委员，中国老年医学中心联盟副主席，中国老年医学保健研究会副会长，中华医学会老年医学分会流行病学组副组长，北京医学会老年医学分会委员、肠内外营养专业委员会委员，国家执业药师资格考试命审题专家。《中国实用内科杂志》常务编委，《中华老年多器官疾病杂志》等编委。\n</p>\n<p>\n	<strong> 出诊时间</strong>：出诊时间：周一上午、周二全天普通门诊（老年综合门诊、消化科门诊）；周三上午外宾门诊（国际医疗部）。\n</p>', '消化内科教授，主任医师，老年示范病房主任', '', 'admin', '0', '1', '2', '2016-07-18 10:16:58', '2016-07-21 16:59:54');
INSERT INTO `articles` VALUES ('21', '10', '宁晓红副教授', '特长：老年综合内科，老年实体肿瘤的内科治疗和临床决策；肿瘤患者的症状控制；缓解患者及家属的身体、心理和精神上的痛苦、纠结；指导家属面对和陪伴末期亲人的细节。 ', 'http://o9sccet84.bkt.clouddn.com/20160718135004928.png', 'http://o9sccet84.bkt.clouddn.com/20160720103503774.png', '<p>\n	<span>宁晓红，女。</span><span><span>1998</span></span><span>年毕业于中国</span><span>协和医科大学并获博士学位，</span><span><span>2002-2014</span></span><span>年从事肿瘤内科工作，担任肿瘤内科副主任医师。现任北京协和医院老年医学科副主任医师。</span><span><span>2004</span></span><span>年前往美国纽约哥伦比亚大学长老会医院肿瘤血液科访问学习。</span><span><span>2012</span></span><span>年带队前往台湾马偈“安宁疗护示范中心”进修舒缓医学（姑息医学）。目前</span>担任中国老年学学会老年肿瘤专业委员会癌症姑息与康复分委秘书长；中国抗癌协会肿瘤心理专业委员会委员；北京抗癌协会癌症及姑息治疗专业委员会常委等职务。同时担任北京协和医学院研究生《舒缓医学》课程负责人，北京协和医学院本科八年制《医患沟通课》和研究生《人文与心理医学在临床》课程教学团队核心成员，北京协和医学院超级课程协会安宁志愿者团队指导教师。\n</p>', '实体肿瘤，内科治疗，临床决策', '', 'admin', '0', '1', '2', '2016-07-18 10:19:57', '2016-07-21 16:59:48');
INSERT INTO `articles` VALUES ('22', '10', '朱鸣雷 医学博士', '常见老年病的诊治、老年人内科疾病的诊治、常见老年综合征的鉴别、老年人的健康管理、疾病预防筛查。老年人功能状态的评估、康复指导。', 'http://o9sccet84.bkt.clouddn.com/20160718134954124.png', 'http://o9sccet84.bkt.clouddn.com/20160720103226619.png', '朱鸣雷，男，2002年毕业于中国协和医科大学并获医学博士学位，2002起在内科从事临床工作，完成了内科所有专科病房、急诊、重症监护室的轮转；2007年任内科总住院医师，2007.10被聘为北京协和医内科主治医师。2008年被医院选派赴美国约翰.霍普金斯大学医学院、老年医学中心进修老年医学，为期半年。回国后一直在老年病房从事临床工作。', '老年人内科疾病，老年综合征的，健康管理，疾病预防筛查', '', 'admin', '0', '1', '2', '2016-07-18 10:23:22', '2016-07-21 16:59:42');
INSERT INTO `articles` VALUES ('23', '10', '康琳 老年医学示范病房主任助理', '老年心血管疾病(包括老年冠心病,高血压,房颤,心衰)的诊断,治疗及药物调整, 老年常见疾病的诊断处理。 ', 'http://o9sccet84.bkt.clouddn.com/20160718134944457.png', 'http://o9sccet84.bkt.clouddn.com/20160720103203888.png', '清华大学、北京协和医学院心血管内科博士；<br />\n协和医学院老年医学系副主任, 北京协和医院老年医学科主任助理；<br />\n北京医师协会老年医学专家委员会委员兼秘书长。<br />\n2007年赴美国约翰•霍普金斯大学进修老年医学专业；<br />\n2012年赴加拿大多伦多大学Baycrest分院参观学习老年医学及全科医学；<br />\n2013年赴美国加州大学旧金山分校进修老年医学及心血管内科；<br />\n2014年赴台北荣民总医院研修老年医学。<br />\n主要从事衰弱、肌少症等老年综合征的临床及科研工作，以及老年常见疾病尤其是心血管疾病如老年高血压、房颤、心衰、高脂血症、冠心病等的诊断、治疗及药物调整，熟悉超声心动图、心电图、心肌核素、冠脉CTA等检查及结果判读。', '老年冠心病，房颤，心衰', '', 'admin', '0', '1', '2', '2016-07-18 10:27:31', '2016-07-21 16:59:36');
INSERT INTO `articles` VALUES ('24', '10', '王秋梅 医学博士', '老年常见疾病、老年综合征及内科常见疾病诊断和治疗；老年全面功能评估和健康指导。 ', 'http://o9sccet84.bkt.clouddn.com/20160718134931812.png', 'http://o9sccet84.bkt.clouddn.com/20160720103147798.png', '<strong>   王秋梅</strong>，女，医学博士，主治医师。2003年毕业于中国协和医科大学并获医学博士学位，2003年起在内科从事临床工作，完成了内科所有专科、急诊、重症监护室的轮转，具有全面、扎实的内科学基础。2007年以来一直从事老年医学临床工作；2008年被医院选派赴美国约翰.霍普金斯大学医学院、老年医学中心进修老年医学。回国后一直在老年病房从事临床工作。熟悉老年患者常见疾病的综合诊治，如慢性阻塞性肺疾病、肺部感染、高血压、冠心病、糖尿病、脑血管疾病、骨质疏松、营养不良、老年痴呆、老年焦虑抑郁状态、前列腺增生等。另外，擅于进行老年人的全面功能状态评估和健康指导。', '老年综合征，老年常见疾病，老年全面功能评估', '', 'admin', '0', '1', '2', '2016-07-18 10:28:51', '2016-07-21 16:59:28');
INSERT INTO `articles` VALUES ('43', '10', '张奉春 内科学系主任', '擅长诊治系统性红斑狼疮、干燥综合征、类风湿关节炎、脊柱关节病、系统性血管炎等各种风湿免疫病。 ', 'http://o9sccet84.bkt.clouddn.com/20160718141058260.png', '', '<p>\n	张奉春，男，北京协和医院风湿免疫科主任，内科学系主任，博士生/博士后导师，中华医学会风湿病学分会常务委员，中华医学会风湿病学分会前主任委员，第六、七届主任委员，中国医师协会风湿免疫病专科医师分会会长，《中华临床免疫和变态反应杂志》主编，《中华风湿病学杂志》副主编，《中国骨与关节杂志》（原《中国骨肿瘤骨病》杂志）副主编。\n</p>', '系统性红斑狼疮，干燥综合征，类风湿关节炎', '', 'admin', '0', '0', '2', '2016-07-18 14:11:02', '2016-07-21 16:57:00');
INSERT INTO `articles` VALUES ('25', '10', '吴瑾 副主任医师', '内科老年常见疾病的诊治，特别是老年高血压、冠心病、血脂异常的诊治。 ', 'http://o9sccet84.bkt.clouddn.com/20160718134920688.png', 'http://o9sccet84.bkt.clouddn.com/20160720103138701.png', '   1986年毕业于北京医科大学医疗系，从事内科临床工作20余年，着重于老年医学专业及心血管专业，擅长内科老年常见疾病的诊治，特别是老年高血压、冠心病、血脂异常的诊治。', '老年高血压，冠心病，血脂异常', '', 'admin', '0', '1', '2', '2016-07-18 10:30:35', '2016-07-21 16:59:22');
INSERT INTO `articles` VALUES ('26', '10', '葛楠 医学硕士', '老年内科常见疾病，特别是呼吸系统感染和肿瘤 ', 'http://o9sccet84.bkt.clouddn.com/20160718134908748.png', 'http://o9sccet84.bkt.clouddn.com/20160720103128485.png', '1996年毕业于东南大学医学院，获医学学士，1998年毕业于上海中医药大学，获中医学学士，2007年获北京协和医学院、清华大学医学硕士。<br />\n从医十余年，中国临床肿瘤协会（CSCO）会员，现主攻老年医学，熟悉老年患者常见疾病的综合诊治，如：慢性阻塞性肺疾病、肺部感染、高血压、冠心病、糖尿病、脑血管疾病、骨质疏松、营养不良、老年痴呆、老年焦虑抑郁状态、前列腺增生等，同时对老年患者的功能状态进行正确的评估，给予生活方面及医疗方面的合理指导。', '老年内科常见疾病，呼吸系统，肿瘤', '', 'admin', '0', '1', '2', '2016-07-18 10:34:30', '2016-07-21 16:59:17');
INSERT INTO `articles` VALUES ('27', '10', '李单青 胸外科主任', '对胸外科各种疾病如肺部肿瘤、食管癌、胃贲门癌、各种食管良性病变、纵隔良恶性肿瘤以及较少见的胸壁、胸骨恶性肿瘤等病种的诊断、治疗方面有较深的造诣。 ', 'http://o9sccet84.bkt.clouddn.com/20160718134811944.png', 'http://o9sccet84.bkt.clouddn.com/20160720103115199.png', '李单青: 北京协和医院  \n胸外科主任，教授，博导、硕导<br />\n兼职： <br />\n全国青联委员<br />\n首都医科大学外聘教授<br />\n中华医学会胸心血管外科学会 \n 委员<br />\n中华医学会胸心血管外科学会第一届青年委员会 常务副主任委员<br />\n中国医师协会胸外科分会 常务委员<br />\n中华医学会北京胸外科学会 \n副主任委员<br />\n中国医师协会胸外科北京分会 常务理事<br />\n北京医学会胸外科分会肺癌专业组 副组长<br />\n大中华胸腔镜外科学院 \n委员<br />\n中国医师协会内镜医师分会、世界内镜医师协会胸外科内镜与微创专业委员会常务理事<br />\n中华医学会医疗鉴定专家<br />\n《The Annals of \nThoracic Surgery》（ATS；胸外科年鉴）、《中华外科杂志》 、《中 \n华胸心血管外科杂志》等多家杂志编委<br />\n专业特长：对胸外科各种疾病如肺部肿瘤、食管癌、胃贲门癌、各种食管良性病变、纵隔良恶性肿瘤以及较少见的胸壁、胸骨恶性肿瘤等病种的诊断、治疗方面有较深的造诣。', '肺部肿瘤，食管癌，胃贲门癌，食管良性病变', '', 'admin', '0', '1', '2', '2016-07-18 10:39:16', '2016-07-21 16:59:12');
INSERT INTO `articles` VALUES ('31', '17', '习近平致电祝贺非洲联盟第二十七届首脑会议召开', '国家主席习近平17日致电在卢旺达首都基加利举行的非洲联盟第二十七届首脑会议，向非洲国家和人民热烈祝贺会议成功召开。', 'http://o9sccet84.bkt.clouddn.com/20160718112317667.jpg', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>央视网消息</strong>(新闻联播)：国家主席习近平17日致电在卢旺达首都基加利举行的非洲联盟第二十七届首脑会议，向非洲国家和人民热烈祝贺会议成功召开。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	习近平指出，非洲联盟是非洲联合自强的旗帜，为促进非洲联合自强和一体化进程发挥了重要作用，衷心祝愿非洲联盟团结带领非洲国家在和平与发展事业中不断取得新的更大成绩。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	习近平强调，去年12月，中非合作论坛约翰内斯堡峰会取得重要成果，开启了中非合作共赢、共同发展的新时代。中非领导人共商合作大计，共同绘就了中非合作发展新蓝图。中方高度重视中非关系发展，将继续秉持真、实、亲、诚对非政策理念和正确义利观，全面推进落实中非“十大合作计划”，推动中非发展全面战略合作伙伴关系不断迈上新台阶，更好造福中非人民\n</p>', '习近平，非洲国家，首脑会议', '', 'admin', '0', '1', '2', '2016-07-18 11:23:21', '2016-07-21 16:58:45');
INSERT INTO `articles` VALUES ('32', '17', '李克强乌兰巴托之行', '“克强节奏”又启动了！这一次是在蒙古国首都乌兰巴托。7月13日—16日，李克强总理对蒙古国进行正式访问，并出席在蒙古国首都乌兰巴托举行的第十一届亚欧首脑会议。', 'http://o9sccet84.bkt.clouddn.com/20160718131159725.png', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	“克强节奏”又启动了！这一次是在蒙古国首都乌兰巴托。7月13日—16日，李克强总理对蒙古国进行正式访问，并出席在蒙古国首都乌兰巴托举行的第十一届亚欧首脑会议。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	首访蒙古国这个中国的重要近邻，李克强总理先后会见蒙总统、总理、国家大呼拉尔主席，并与蒙总理共同见证双方签署涵盖贸易、经济技术、基础设施建设、广播电视等领域的15份双边协议。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	在成立20周年的第十一届亚欧首脑会议上，李克强总理在引导性发言中提出三大倡议，为亚欧合作在新的10年实现新跨越贡献“中国方案”。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	而在这些日程之外，总理还挤出全部空隙时间，安排了十多场双边会见，并创造性地拓展“过道外交”、“看台外交”和“宴会外交”，与多国领导人广泛沟通、深入交流，中国在南海问题上的立场赢得了亚欧各国的广泛支持。大会最终通过的《乌兰巴托宣言》和《主席声明》，南海问题均未提及。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	“克强节奏”持续4天，不仅在经贸领域收获颇丰，尤其令人印象深刻的是，总理以分秒计、夙夜在公，为国家利益辛劳奔忙的这20个瞬间。\n</p>', '李克强，蒙古，首脑会议', '', 'admin', '0', '1', '2', '2016-07-18 13:12:05', '2016-07-21 16:58:39');
INSERT INTO `articles` VALUES ('33', '17', '中国海军1天内入列两艘2万吨巨舰 加入南海舰队', '7月15日上午，中国自行设计制造的新型远洋综合补给舰洪湖舰、骆马湖舰入列命名仪式在广东湛江某军港举行。自此，两艘远海综合补给舰正式加入海军南海舰队战斗序列，同时也标志着人民海军远洋遂行任务能力得到进一步拓展。', 'http://o9sccet84.bkt.clouddn.com/20160718131356953.png', '', '<span style=\"font-family:宋体;font-size:14px;line-height:28px;background-color:#FFFFFF;\">7月15日上午，中国自行设计制造的新型远洋综合补给舰洪湖舰、骆马湖舰入列命名仪式在广东湛江某军港举行。自此，两艘远海综合补给舰正式加入海军南海舰队战斗序列，同时也标志着人民海军远洋遂行任务能力得到进一步拓展。</span>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	据中新社报道，中国自行设计制造的新型远洋综合补给舰洪湖舰、骆马湖舰入列命名仪式，7月15日在广东湛江某军港举行。自此，两艘远洋综合补给舰正式加入海军南海舰队战斗序列，同时也标志着中国海军遂行远海任务能力得到进一步拓展。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	上午9时30分，在雄壮的国歌声中，综合补给舰洪湖舰、骆马湖舰入列命名授旗仪式开始，海军南海舰队司令员沈金龙为洪湖舰和骆马湖舰分别授旗，并颁发命名证书。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	洪湖舰舰长刘永新代表舰员发言说，要抓紧练兵备战，让新装备更好更快形成战斗力，在维护国家海洋权益的征程中建功立业，肩负起应有的责任与担当。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	今年5月，洪湖舰就已参加南海舰队组织的远海实兵演练，赴西沙、南沙、南海、印度洋、西太平洋等海域开展实战条件下的综合补给演练，为训练编队提供后勤补给，实际检验装备性能。两艘新型综合补给舰的正式入列，将有效提升中国海军远海作战支援保障作战能力，为维护国家主权和海洋权益发挥重要作用。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	某作战支援舰支队支队长朱建达告诉记者，近年来，海军装备现代化水平不断提升，支队的装备更新率也在同步加快，仅过去一年，我们就有8艘新型舰艇入列。伴随着装备更新换代的步伐，支队官兵坚持从难从严开展实战化训练，实现了人与装备的最佳结合。面对复杂的海上斗争形势，我们有信心有能力应对海上各种安全威胁与挑战。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	洪湖舰和骆马湖舰均属同一型舰，满载排水量20000余吨，是中国自行设计生产的新型远洋综合补给舰，舷号分别为963、964，两舰采用了中国较为先进的海上补给技术，具备横向、纵向、立体补给功能，可同时对多艘不同类型舰艇进行补给，有较强的远洋综合补给能力和一定的对海、对空防御作战能力。平时主要伴随驱护舰执行编队远航及出访任务，战时加入海上机动编队，对伴随舰船实施燃油、淡水、食品、弹药等综合补给。该型舰还具有现代化程度高、任务拓展空间大等特点，除了担负综合补给任务外，还可执行护航、撤侨、搜救、海上医疗救护等多样化非战争军事行动。\n</p>', '中国南海，人民海军，菲律宾', '', 'admin', '1', '1', '2', '2016-07-18 13:14:00', '2016-07-21 16:58:34');
INSERT INTO `articles` VALUES ('34', '17', '土耳其政变逾290人死 军队和司法系统6000人被捕', '7月17日消息，当地时间7月16日，土耳其政府宣布挫败由部分军方人士发动的军事政变。图为当地民众集会谴责军事政变。', 'http://o9sccet84.bkt.clouddn.com/20160718131602473.png', '', '<span style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;line-height:28px;background-color:#FFFFFF;\">7月17日消息，当地时间7月16日，土耳其政府宣布挫败由部分军方人士发动的军事政变。图为当地民众集会谴责军事政变。</span>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	中新网7月18日电 据外媒报道，土耳其17日继续扩大对军事政变支持者的逮捕，军队和司法系统中被捕的人数达到6000人，政变导致的死亡人数超过290人。政府表示，国家和经济都得到控制。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	土耳其总统埃尔多安和政府指责流亡美国的穆斯林阿訇法土拉·葛兰(Fethullah Gulen)发起并策划了整个政变。葛兰以前是埃尔多安的盟友，埃尔多安指责他试图在法院、警察、军队和媒体中创立一个“平行架构”。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	埃尔多安支持者聚集在他的伊斯坦布尔寓所前，要求对政变策划者处以死刑惩罚，土耳其早在2004年为了加入欧盟已废除了死刑。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	“我们不能忽视这个需求，”埃尔多安对群众说道，“在民主体制下，人民不管说什么，都要实现它。”\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	他说，以葛兰为首的“恐怖集团”已经“毁掉了”军队，每个军阶都有人被拘捕，对这种“病毒”的肃清将继续下去。但葛兰否认与这次政变有瓜葛。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	埃尔多安呼吁民众留在街道上支持政府直至17日夜间，他的支持者挤满了广场和大街小巷，按响喇叭并挥舞旗帜。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<span class=\"infoMblog\"><a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/wj_xlt#pref=qqcom.keyword\" target=\"_blank\">外交部</a></span>公布的死亡人数上调至290人以上，其中有超过100人为反叛者，此外还有1400人受伤。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	预计土耳其即将展开大规模镇压活动，欧洲政要向埃尔多安发出警告，称政变企图并不能让他获得一张空白支票，可以为所欲为无视法律，他在巩固国内地位的同时，有可能在国际上陷于孤立。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	NTV广播公司援引司法部长Bekir Bozdag的话称，预计会有更多人被捕。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	一名官员称，被捕者中有Incirlik空军基地指挥官Bekir Ercan Van将军。美国飞机即是从Incirlik基地起飞对叙利亚和伊拉克境内的伊斯兰国武装分子发动空袭的。此外<span class=\"infoMblog\"><a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/CNN_NEWS#pref=qqcom.keyword\" target=\"_blank\">CNN</a></span>土耳其广播电台称，埃尔多安的首席军事助手亦遭拘捕。\n</p>', '土耳其，军事，政变', '', 'admin', '0', '1', '2', '2016-07-18 13:16:06', '2016-07-21 16:57:55');
INSERT INTO `articles` VALUES ('35', '18', '走出防晒误区 夏天做一个水灵灵的白美人', '炎炎夏日里，最怕的就是被太阳晒伤。防晒成为首要任务。很多人在使用防晒霜的时候，固定在一个预防被太阳晒伤的思维，而导致防晒效果大大扣分。下面就来看下夏季防晒误区有哪些？', 'http://o9sccet84.bkt.clouddn.com/20160718131942261.png', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>误区一：晒晒更健康</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	常有人自信满满地“炫耀”：我的皮肤就是晒不黑，所以不用防晒。殊不知，防晒不仅仅是为了防止皮肤“变黑”，更是为了防止容颜衰老。紫外线中含有大量有害物质，对皮肤有很大伤害，是令皮肤老化的杀手。因此，防晒也就是防衰老。紫外线中含有UVA、UVB和UVC，且一年四季都存在，不论白天、黑夜、雨天或阴天。紫外线是造成衰老的主要原因，却也往往最容易被女性忽视。被紫外线灼伤后的皮肤，会有发红、变黑、长斑的不良反应。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>误区二：阴天时云层很厚，紫外线就不会伤害到皮肤了</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	真实情况：云层对紫外线来说几乎起不到任何隔离作用，90%的紫外线都能穿透云层，惟有昏暗而又厚重的雨云层才能阻止部分紫外线。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>误区三：偶尔几次忘记涂防晒品，不会对皮肤有太大的影响</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	真实情况：日晒是可以累积的，因此虽然只是间接性地接受日晒，对皮肤的伤害却会长期积累下来，或许无法立刻看到后果，但时间长了就会造成肌肤晒黑、脸上出现斑点、皮肤失去弹性、产生皱纹、老化等现象。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>误区四：我的皮肤已经被晒黑了，再涂防晒霜也无济于事</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	真实情况：皮肤晒后呈棕黄色，表明皮肤进入自我保护状态。皮肤的增厚和黑色素的产生是皮肤自我保护的表现。但黑色素只能部分吸收紫外线B，起隔离作用，使肌肤不受损伤，却无吸收紫外线A的功能。所以，到户外，太阳与皮肤间的隔离屏障是必不可少的。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>误区五：短时间外出不用涂了</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	还有的女生会以为我只出去一会儿，就没有必要涂了吧，如果你是早晚出去的还好一些，但是你上午或者是下午出门即使时间很短也要涂的。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>误区六：眼部不用防晒产品</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	最脆弱的眼周肌肤的防晒工程经常被忽略，看看很多人眼周的晒斑就足以证明这一点。也有人将面部防晒产品直接涂到眼部，殊不知含有SPF防晒指数的面部产品，对眼部肌肤来说是不堪重负的，涂抹后有可能造成极大的不适甚至会产生敏感现象。一副太阳眼镜，质量好的防晒眼霜，可用于眼部的遮瑕膏都可充当眼部的防晒品。\n</p>', '防晒，误区，美白', '', 'admin', '0', '1', '2', '2016-07-18 13:19:49', '2016-07-21 16:57:49');
INSERT INTO `articles` VALUES ('36', '18', '你知道吗？经常吃核桃到底能不能补脑？', '山西读者杨女士问：最近用脑量比较大，我看有人说核桃有助补脑，但也有人表示，核桃补脑的说法属于以形补形，没有科学依据。想问问专家，吃核桃到底能不能补脑？', 'http://o9sccet84.bkt.clouddn.com/20160718132154881.png', '', '<span style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;line-height:28px;background-color:#FFFFFF;\">山西读者杨女士问：最近用脑量比较大，我看有人说核桃有助补脑，但也有人表示，核桃补脑的说法属于以形补形，没有科学依据。想问问专家，吃核桃到底能不能补脑？</span>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	中国农业大学食品科学与营养工程学院副教授范志红解答：一些近期的科学研究提示，不能完全否定核桃对长期的大脑健康和认知功能有一定益处。有国外研究表明，增加坚果类食物的摄入，特别是核桃，对于中老年人的认知能力有改善作用。较多的坚果摄入，与记忆力、认知灵活性、信息处理速度，以及整体的认知功能改善有关。另一项为期3年的研究表明，每天吃初榨橄榄油+30克坚果（15克核桃、7.5克扁桃仁、7.5克榛子），相比于仅吃初榨橄榄油而无坚果，或者吃低脂肪饮食的人，能够持续提升血浆中脑源性神经营养因子的浓度，有对抗抑郁，以及衰老带来的认知退化的作用。从这些方面来看，在膳食中加入一些核桃或核桃油，确实能够对身体起到有益效果，进而可以间接促进大脑功能的健康状态。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	为什么核桃可能起到这样的健康作用呢？按照美国的相关研究，核桃是各种坚果当中抗氧化能力最强的一个品种，除了果仁中的维生素E之外，主要是因为果仁外面包裹的那层褐皮富含多酚类物质。大脑组织对氧化损伤十分敏感，摄入充足抗氧化物质是必要的。同时，核桃还含有α亚麻酸，它能够在身体中被转换为DHA，而这种成分对婴幼儿大脑发育很重要，对老年人延缓大脑衰老等也有着重要意义。所以，“核桃补脑”的说法有物质基础。但是，这些有益营养成分并非核桃所特有，核桃也不是其最好的食物来源。事实是，其他坚果、蔬菜、水果等食物也富含抗氧化物质，含量绝对不输给核桃，而且每天的摄入量更大。上述研究中，发现的是坚果整体的好处，而不仅仅是核桃的好处。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	需要特别说明的是，科学界没有对“补脑”的准确定义。普通消费者对“补脑”的期待是：能够提升智力，快速缓解大脑疲劳，增强学习、工作能力等。而科学主要的研究领域则是大脑的“发育”和“衰老”。这和消费者的期望并不一致。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	研究发现，长期吃核桃等坚果能改善一些与大脑功能相关的指标，和人们所期待的核桃“补脑”功能并不是一回事情。到现在为止，并没有直接的研究证据表明，吃了核桃之后，就能短期内提高人的智商，或者提高考试成绩。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	而且，多数人的智力会在20~30岁左右达到顶峰，之后则很难提升。正确的饮食包括日常摄入少量核桃等坚果，确实有利延缓大脑的衰老过程，减少包括中风在内的疾病风险。但那种吃了马上就“反应快了，记忆好了，智力蹭蹭往上跑了”之类的效果，只是我们的一种美好想象。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	总而言之，核桃是一种营养不错的坚果，细水长流地每天少量吃点坚果类食物，对于改善心脑血管相关指标，从而让大脑老得慢一些，可能确实会有点长期效果。但这并不意味着吃核桃能够达到人们对“补脑”的期待。\n</p>', '核桃，补脑，科学依据', '', 'admin', '0', '1', '2', '2016-07-18 13:21:57', '2016-07-21 16:57:43');
INSERT INTO `articles` VALUES ('37', '18', '艾滋病感染者信息泄露 疾控中心：已报案', '针对近日有艾滋病感染者疑遭电话诈骗，个人信息或遭泄露一事，中国疾病预防控制中心表示，已报请公安部门立案侦查。', 'http://o9sccet84.bkt.clouddn.com/20160718132318687.png', '', '<span style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;line-height:28px;background-color:#FFFFFF;\">针对近日有艾滋病感染者疑遭电话诈骗，个人信息或遭泄露一事，中国疾病预防控制中心表示，已报请公安部门立案侦查。\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	据媒体报道，近来，有艾滋病感染者接到可疑电话，自称是政府部门工作人员，以给感染者发放补助为名，索要手续费，感染者怀疑个人信息遭泄露。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	对此，中国疾病预防控制中心相关负责人表示，艾滋病感染者的个人隐私信息受国家法律保护。艾滋病防治条例第39条规定，未经感染者本人或其监护人同意，任何单位和个人不得公开艾滋病病毒感染者和艾滋病病人及其家属的姓名、住址、病史资料以及其他可能推断出其具体身份的信息。不法分子盗用艾滋病感染者个人信息、实施电话诈骗，是违法犯罪行为，中国疾控中心已报请公安部门立案侦查，依法进行严厉打击。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	中国疾控中心相关负责人表示，我国高度重视艾滋病感染者相关信息安全工作。国家艾滋病感染者相关信息系统被列为国家网络信息重点安全保护对象。发现诈骗情况后，中国疾控中心立即启动了信息系统安全应急响应，对相关信息系统开展信息安全排查，并进一步提升了相关加密措施。下一步，中国疾控中心将积极配合公安部门尽快破案，也同时呼吁接到类似诈骗电话的感染者，及时向当地公安机关报告，并配合公安部门调查。\n</p>\n</span>', '电话诈骗，个人信息，中国疾病预防控制中心', '', 'admin', '0', '1', '2', '2016-07-18 13:23:23', '2016-07-21 16:57:37');
INSERT INTO `articles` VALUES ('38', '18', '“十三五”医保住院报销比例将稳定在75%', '人社部昨天公布了《人力资源和社会保障事业发展“十三五”规划纲要》，透露“十三五”时期我国要制定出台渐进式延迟退休年龄方案，并将生育保险和基本医疗保险合并实施。', 'http://o9sccet84.bkt.clouddn.com/20160718132454391.png', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	“十三五”时期我国要制定出台渐进式延迟退休年龄方案。人社部部长尹蔚民13日表示，备受关注的延迟退休将坚持三个原则，一是小步慢走、逐步到位；二是区分对待，分步实施；三是事先预告，做好公示。按照工作计划，今年将拿出延迟退休方案，但还需要履行有关程序，然后再向社会公布。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	在此期间，我国要实现职工基础养老金全国统筹。制定公务员和参照公务员法管理事业单位、社会团体工作人员工伤保险政策。探索建立长期护理保险制度，通过在部分地方开展试点，探索建立以社会互助共济方式筹集资金，为长期失能人员的基本生活照料和与基本生活密切相关的医疗护理提供基金或服务保障的社会保险制度。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	我国要将职工和城乡居民基本医疗保险政策范围内住院费用支付比例稳定在75%左右。适当降低社会保险费率。推进划转部分国有资本充实社会保险基金工作。拓宽社会保险基金投资渠道，加强风险管理，提高投资回报率。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	“十三五”时期还要实施全民参保计划，基本养老保险参保率要达到90%，基本医疗保险参保率稳定在95%以上，基本实现法定人员全覆盖。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	另外，“十三五”期间我国要实现城镇新增就业5000万人以上，城镇登记失业率控制在5%以内。\n</p>', '生育保险，基本医疗保险，人社部', '', 'admin', '2', '1', '2', '2016-07-18 13:24:57', '2016-07-21 16:57:30');
INSERT INTO `articles` VALUES ('39', '2', '预见更好的自己 一场骄阳下的公益健康行', '7月2日是一个特殊的日子,它不仅是国际体育记者节,还恰逢七月第一个周六的国际合作节。在羊城创意产业园,近千名参赛者顶着烈日齐聚园区,参与这场以“预见更好的自己”为主题的趣味跑步运动会。这场倡导合作与健康精神的活动,由羊城晚报报业集团与NU SKIN如新(中国)日用保健品有限公司联合主办,金羊网协办。', 'http://o9sccet84.bkt.clouddn.com/20160719131850503.png', '', '<p>\n	7月2日是一个特殊的日子,它不仅是国际体育记者节,还恰逢七月第一个周六的国际合作节。在羊城创意产业园,近千名参赛者顶着烈日齐聚园区,参与这场以“预见更好的自己”为主题的趣味跑步运动会。这场倡导合作与健康精神的活动,由羊城晚报报业集团与NU SKIN如新(中国)日用保健品有限公司联合主办,金羊网协办。\n</p>\n<p>\n	■新快报记者 庞倩影 实习生 张素芬\n</p>\n<p>\n	各团队斗智斗勇 赛场氛围激烈\n</p>\n<p>\n	比赛将5人分为一组,其中一名为队长,活动参与者主要是体育爱好者和媒体人。他们需要围绕既定的路线奔跑,并且接力通关,陆续完成寻宝、美工、线上游戏等挑战环节。\n</p>\n<p>\n	上午九时,主持人宣布活动开始。数百人在教练的带领下一同完成赛前热身运动,随后一声枪鸣,正式开赛。\n</p>\n<p>\n	比赛开始短短几分钟,就有几位队长率先完成任务A“沿着外围赛道跑一圈”,抵达任务区完成涂色,并且领取了下一个任务卡。紧接着,团队所有成员倾巢而出,各施所能。\n</p>\n<p>\n	所有的团队成员进入了“疯狂大搜索”的模式,整个园区随处可见的是团队成员神色匆忙地寻找任务,不放过园区内每个可能的角落。前一天刚下过雨,园区的草地还是比较潮湿,有些任务所在地一片泥泞,但参赛者们并没有放缓步伐。\n</p>\n<p>\n	在任务B区,早早就聚集了大批团队,各团队成员聚集在标志物前拍照,现场拥挤。任务C区也十分热闹,因为现场太多类似的标志物,让团队找起来有些许难度。还有一些团队根据前两个任务的完成程度在任务C区重新商量制定行动计划。在最后的E任务区,团队所有成员跟宁泽涛拍大合照并发朋友圈,凭截图让裁判裁定后, 才能在涂色区完成最后的涂色。\n</p>\n<p>\n	让跑步有趣起来 寓快乐于运动\n</p>\n<p>\n	经过1个小时的角逐,率先完成任务的冠、亚、季军陆续出现。他们分别是017团队(冠军),0147团队(亚军),0139团队(季军)。完成任务的团队都聚集到舞台前面,队员们大汗淋漓,头发、运动衫全都湿透,但脸上笑容满满。\n</p>\n<p>\n	参加过柏林马拉松比赛的林清清以一名记者的身份参加了此次公益健康行记者跑团,她认为此次团队协作的比赛模式十分不错。她表示,团队赛制是国际上十分重要的赛制,也是不少比赛的卖点,很多人会专门为了团队赛制去参加一个比赛,团队赛比起个人赛会更“热血”一些。\n</p>\n<p>\n	作为参赛记者,她认为这次“预见更好的自己”健康行活动是一种穿插了有趣的定向越野活动的“轻运动”,现代都市人尤其是媒体人的工作压力相对较大,有些人甚至出现过劳“猝死”,“像这样利用周末进行‘轻运动’是挺好的放松方式,既有趣又健康,也比较有安全保障。”林清清说。\n</p>', '国际合作节，国际体育记者节，跑步运动会', '', 'admin', '0', '1', '2', '2016-07-18 13:30:51', '2016-07-21 16:57:24');
INSERT INTO `articles` VALUES ('40', '2', '“全民健康促进工程 心血管病筛查防治走进千县万村公益活动暨心脑血管健康筛查车捐赠仪式”在京举行', '由人民政协网和北京远程光明公益基金会联合主办的“全民健康促进工程——心血管病筛查防治走进千县万村公益活动暨心脑血管健康筛查车捐赠仪式”25日上午在全国政协礼堂举行。此次“心血管病筛查防治走进千县万村公益活动暨心脑血管健康筛查车捐赠”即是“全民健康促进工程”系列活动的一项。', 'http://o9sccet84.bkt.clouddn.com/20160719131759166.png', '', '<p>\n	由人民政协网和北京远程光明公益基金会联合主办的“全民健康促进工程——心血管病筛查防治走进千县万村公益活动暨心脑血管健康筛查车捐赠仪式”25日上午在全国政协礼堂举行。此次“心血管病筛查防治走进千县万村公益活动暨心脑血管健康筛查车捐赠”即是“全民健康促进工程”系列活动的一项。\n</p>\n<p>\n	中国经济网北京6月25日讯由人民政协网和北京远程光明公益基金会联合主办的“全民健康促进工程——心血管病筛查防治走进千县万村公益活动暨心脑血管健康筛查车捐赠仪式”25日上午在全国政协礼堂举行。据介绍，“全民健康促进工程”是由人民政协网和北京远程光明公益基金会联合组织发起的以“打造健康中国、构建和谐社会”为主题的大型公益活动。活动旨在凝聚和发挥政协组织、政协委员、公益组织、新闻媒体、医学专家、基层医院、爱心企业和社会各界的力量，通过搭建医疗慈善公益帮扶救助平台，在全国范围内开展医疗公益帮扶救助等形式，帮助基层医疗机构改善硬件设施、提升医疗水平，推动全民健康意识的提高。此次“心血管病筛查防治走进千县万村公益活动暨心脑血管健康筛查车捐赠”即是“全民健康促进工程”系列活动的一项。活动致力于用5年时间，组织、发动社会公益资源，通过在1000个县深入乡村开展健康知识宣传、搭建心血管病防治公益帮扶救助平台、开展心脑血管等慢性疾病危险因素的筛查等方式，促进城乡居民健康状况改善，减少不同地区健康状况差异，遏制心血管病持续增长的态势。捐赠仪式上，主办方分别向20家基层医疗机构捐赠了首批20台“全民健康促进工程心脑血管健康筛查车”。来自医疗卫生领域的专家、学者、基层医疗单位负责人、医务人员以及媒体记者等共计300余人出席活动。\n</p>', '全民健康，心脑血管健康筛查，捐赠仪式', '', 'admin', '0', '1', '2', '2016-07-18 13:32:50', '2016-07-21 16:57:19');
INSERT INTO `articles` VALUES ('41', '2', '2016儿童心理健康公益活动暖心启动', '6月14日，由北京大学第六医院、北大医疗儿童发展中心共同发起的“拿什么爱你，我的孩子”2016儿童心理健康公益活动在北京大学医学部会议中心正式启动。此次公益活动将从6月持续到年底，目的是为了唤醒社会对孩子心理健康问题的关注、正视和重视，科普儿童心理健康相关知识，给孩子们创造一个良好的身心成长环境。', 'http://o9sccet84.bkt.clouddn.com/20160719131821653.png', '', '<p>\n	6月14日，由北京大学第六医院、北大医疗儿童发展中心共同发起的“拿什么爱你，我的孩子”2016儿童心理健康公益活动在北京大学医学部会议中心正式启动。此次公益活动将从6月持续到年底，目的是为了唤醒社会对孩子心理健康问题的关注、正视和重视，科普儿童心理健康相关知识，给孩子们创造一个良好的身心成长环境。\n</p>\n<p>\n	中国残疾人联合会副主席黄悦勤、北京大学第六医院院长陆林、北大医疗产业集团CEO吕和东等嘉宾出席了启动仪式并致辞。吕和东指出，北大医疗始终以维护国人身心全方位健康为己任，而北大医疗儿童发展中心就是北大医疗在精神卫生领域的重要布局。陆林表示，儿童心理健康事业是整个医疗行业的重要事业，孩子的身心健康更是关系着国家的未来。作为这场活动的发起方之一，北大六院将为该公益活动全程提供专业指导和专家资源支持。\n</p>\n<p>\n	资料显示，该公益活动包括线上和线下两部分。线上部分将通过微信万人微课堂的形式，依托权威媒体和专家，进行科普宣传，普及儿童心理健康知识。同时进行的线下部分，将走进参与该公益活动的北京市中小学、社区，开展大型儿童心理健康知识科普、专家现场咨询等活动，进一步将儿童心理健康的理念和知识带到孩子、老师、家长身边。\n</p>\n<p>\n	记者从北大医疗儿童发展中心相关专家处了解到：在世界范围内儿童心理障碍的患病率是20%;在我国，儿童、青少年大约有4.2亿，而据有关调查资料显示我国儿童青少年中精神、行为、发育障碍的患病率达 14.89%，由此估计，我国大约有精神障碍患儿4000万。但是，其中仅有5.8%的患病儿童少年曾经到儿童精神科或心理卫生门诊就诊。\n</p>\n<p>\n	儿童心理问题不仅关乎孩子的未来，还牵动着家庭的幸福，以及整个社会的健康发展。近两年来，政府已经开始逐步加强对儿童心理健康的关注，但目前整个社会对儿童心理问题的认知度仍然不足，且缺乏必要、有效的发现、引导机制。在这样的大环境下，北大医疗儿童发展中心联合北京大学第六医院及北大医疗产业集团共同启动了此次公益活动。\n</p>\n<p>\n	据了解，北大医疗儿童发展中心，是北大医疗脑健康公司旗下专注儿童精神心理健康的子品牌，关注儿童成长各时期的心理健康问题，提供咨询、评估、干预和培训一体化的专业服务。依托北京大学第六医院、北京大学第六医院儿童心理卫生中心顶级专家团队，及持有国际儿童教育资格认证的专家团队的支持与指导，北大医疗儿童发展中心将在原有业务基础上树立起儿童心理健康公益事业的新标杆。\n</p>\n<p>\n	“通过这次大型公益活动，我们期望普及民众对儿童心理健康领域的科学认知，让孩子能够获得全方面的健康成长，”谈到发起此次公益活动的意义，北大医疗脑健康公司总经理王磊告诉记者，“北大医疗脑健康公司是北大医疗产业集团与北京大学第六医院共同合作成立的，我们有责任承担起推动国内精神卫生事业发展、守护国民心理健康的职责。”\n</p>', '留守儿童，公益，医疗', '', 'admin', '0', '1', '2', '2016-07-18 13:38:33', '2016-07-21 16:57:13');
INSERT INTO `articles` VALUES ('42', '2', '制度创新促慈善组织健康发展', '制度创新促慈善组织健康发展\n \n——中华慈善总会学习贯彻慈善法培训在浙江宁波召开\n', 'http://o9sccet84.bkt.clouddn.com/20160719131729359.jpg', '', '<p>\n	制度创新促慈善组织健康发展\n</p>\n<p>\n	——中华慈善总会学习贯彻慈善法培训在浙江宁波召开\n</p>\n<p>\n	随着《慈善法》的出台，标志着我国的慈善事业进入了一个依法治理的健康发展时期。如何贯彻和落实《慈善法》及相关配套法律法规，是全国慈善工作者当前最重要的任务。2016年6月7日，中华慈善总会学习贯彻《慈善法》培训在浙江省宁波市召开。《慈善公益报》记者在培训会上获悉，本次《慈善法》培训是中华慈善总会落实民政部关于积极宣传《慈善法》的部署，充分发挥自身优势宣传《慈善法》的重要举措之一。\n</p>\n<p>\n	继承弘扬慈善文化 依法开拓慈善事业\n</p>\n<p>\n	浙江省政协常务副主席、浙江省慈善总会会长陈加元在会上致欢迎辞。他说：“《慈善法》的颁布施行是我国慈善事业发展的重要里程碑。乐善好施是中华民族的传统美德，《慈善法》出台，促进了慈善事业大众化、生活化、秩序化发展，对慈善工作制度化、规范化、法治化起到重要保障作用。”\n</p>\n<p>\n	中华慈善总会会长李本公出席培训并就全国慈善会系统如何认识和贯彻《慈善法》提出重要指导性意见。他指出，《慈善法》是以法律的形式把正能量和主流思想确定下来，建立了中国特色慈善事业的法律主体框架。《慈善法》的出台标志着我国慈善事业进入全面法治时代，大大有利于中华慈善总会和全国慈善会系统的发展。慈善会系统要忠于法律，认真学法知法，做依法开展慈善的模范。\n</p>\n<p>\n	李本公表示，《慈善法》有很多制度创新和更加严格的制度和规定，需要结合慈善会实际情况深入研究，从思想上、工作上适应法律的规定，做到既要按照《慈善法》的规定进一步规范、又能依据《慈善法》的制度创新去大胆开拓。特别是要积极拓宽眼界扩大慈善内涵，研究、试行慈善信托，不断拓宽合作领域、扩大合作对象群体，高度重视项目协议的起草、推敲和审议，努力使《慈善法》的各项具体规定在慈善会系统落地。\n</p>\n<p>\n	李本公还强调，学习《慈善法》是我们当前和今后一个时期的重要任务，把慈善法学习好贯彻好也需要一个渐进的过程，关键是要起好步。与此同时，各地都在结合各自实际认真学习，对《慈善法》的基本内容有了越来越深的认识。下一步则需要在此基础上，进入深层次的研读和结合具体工作的贯彻阶段。\n</p>\n<p>\n	制度保障健康发展 因地制宜创新慈善\n</p>\n<p>\n	培训会上，民政部民间组织服务中心党委书记刘忠祥与中国劳动关系学院公共管理系副主任杨思斌教授分别对《慈善法》进行了深度解析。刘忠祥书记总结了《慈善法》的十大创新亮点，并对《慈善法》实施后慈善组织的管理和运作提出了重要意见。他表示，《慈善法》是一个集体智慧的结晶，它的颁布实施有很多制度创新，由于法律位阶比较高，必将促进慈善事业特别是慈善组织健康发展。\n</p>\n<p>\n	杨思斌教授着重从法学的角度，通过理论与案例相结合的方式，详细讲解了如何运用法治思维运作慈善组织、开展慈善活动。\n</p>\n<p>\n	会上，各地与会代表积极提出问题、发表看法，切实体现了促进交流、增进共识、共同进步的培训初衷。\n</p>\n<p>\n	浙江省慈善总会副会长李刚介绍了浙江慈善工作近年来运用市场理念开拓创新的做法，并提出将以需求为导向、市场为平台，强化创新实践，拓展工作领域，推动劝募市场化，努力打造“现代大慈善”的发展目标。\n</p>\n<p>\n	湖北省慈善总会秘书长谭光华介绍了湖北省慈善总会在建制度、搭平台、抓服务方面的努力，并着重介绍了互联网时代如何运用互联网思维做好慈善服务与创新。\n</p>\n<p>\n	持久学习慈善法精髓 开创慈善事业新局面\n</p>\n<p>\n	此次培训，有来自全国各省、自治区、直辖市、新疆生产建设兵团、各计划单列市、省会城市、部分地市慈善会副秘书长以上领导以及浙江省内慈善组织代表共计400余人参加培训。培训由中华慈善总会秘书长边志伟、会长助理陈砚秋主持，中华慈善总会常务副会长王树峰作总结报告。\n</p>\n<p>\n	王树峰在培训总结中讲到，本次会议紧紧围绕《慈善法》解读，并结合慈善会自身特点，深入研究新形势下依法行善、依法创新的重要举措，取得了重要的成果。他强调，全国慈善会系统要继续持久深入地学习领会《慈善法》;要以《慈善法》为依托，规范与拓展慈善工作;要勇担重任，积极组织宣传《慈善法》精神，将本次培训精神深入贯彻落实下去。参训人员纷纷表示,通过此次培训更加深刻理解了《慈善法》，进一步增强了学习宣传好《慈善法》的信心，为贯彻实施好《慈善法》奠定了理论基础。希望全国慈善工作者以本次培训为新的起点，学法、用法，团结奋进，精诚合作，为全面推动我国慈善事业走进法治新时代做出积极的贡献。\n</p>', '慈善组织，浙江宁波', '', 'admin', '0', '1', '2', '2016-07-18 13:46:06', '2016-07-21 16:57:07');
INSERT INTO `articles` VALUES ('44', '10', '严晓伟 内科学系副主任', '临床工作着重于各种心血管病危险因子（高血压、脂质代谢异常、糖尿病等）的防治、心力衰竭诊治、血栓栓塞性疾病的抗凝和抗血小板治疗、动脉粥样硬化心脑血管病的一级预防和二级预防、以及全身性疾病累及心脏的诊治等。主要研究领域包括：脂质代谢异常、动脉粥样硬化发病机制及预防和心血管病临床药理。', 'http://o9sccet84.bkt.clouddn.com/20160718141204927.jpg', 'http://o9sccet84.bkt.clouddn.com/20160718141204927.jpg', '<p>\n	1985年7月毕业于上海医科大学医学系。1990年6月在中国协和医科大学获医学博士学位。2002年6月晋升为北京协和医院心内科正主任医师、教授。1995年9月至1998年4月在瑞士苏黎世大学医院心内科进行博士后研究，主要从事心血管临床药理和血管内皮功能方面的研究。在国内外医学杂志共发表综述和论著120余篇，著书约80万余字,主编/主译专著5部(其中副主编2部)。\n</p>\n<p>\n	现任北京协和医院心内科副主任，内科学系副主任，博士研究生导师，中华医学会理事，卫生部心血管病防治研究中心专家委员会委员，中国老年学学会心脑血管病专业委员会常务理事，中华医学会心血管病分会动脉粥样硬化与冠心病学组成员，中国医师学会心血管病分会专家委员会委员，中华医学会科学普及分会第八届委员会委员，中国医师学会高血压病分会专家委员会委员，备战2012年伦敦奥运会国家队医疗专家。\n</p>\n<p>\n	目前是《中华心血管病杂志》、《中国循环杂志》、《中国介入心血管病杂志》、《中华高血压杂志》、《中华老年心脑血管病杂志》、《基础医学与临床》、《临床心血管病杂志》、《国际心血管病杂志－原国外医学心血管病分册》、《山东医药》、《国际循环》、《JACC中文版》、《Eur \nHeart J中文版》、《BMJ中文版》和《中国心血管杂志》编委,《中国医刊》特邀编委。Member of International Society of \nHypertension, Member of European Society of Heart Failure。\n</p>\n<p>\n	临床工作着重于各种心血管病危险因子（高血压、脂质代谢异常、糖尿病等）的防治、心力衰竭诊治、血栓栓塞性疾病的抗凝和抗血小板治疗、动脉粥样硬化心脑血管病的一级预防和二级预防、以及全身性疾病累及心脏的诊治等。\n</p>\n<p>\n	主要研究领域包括：脂质代谢异常、动脉粥样硬化发病机制及预防和心血管病临床药理。\n</p>', '高血压，脂质代谢异常，糖尿病', '', 'admin', '0', '0', '2', '2016-07-18 14:12:06', '2016-07-21 16:56:37');
INSERT INTO `articles` VALUES ('45', '3', '夫妻吃2公斤小龙虾 致肌溶解走不动路', '每年的5-8月是食用小龙虾的旺季，有统计南京人每天要吃掉好几十吨的小龙虾。当然，集万千宠爱于一身的小龙虾也招来不少事，前两天，东南大学附属中大医院急诊科就收治了一对因为吃小龙虾导致横纹肌溶解的夫妻。', 'http://o9sccet84.bkt.clouddn.com/20160719131645460.png', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>吃完2公斤小龙虾走不动路</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	王女士(化名)今年55岁，前两天突然想吃小龙虾了，就去菜场买了2公斤回家做。王女士心想着自己烧，不仅洗得干净，还不会添加乱七八糟的东西，安全有保障。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	香喷喷的小龙虾烧好后，夫妻俩津津有味地品尝起来，王女士吃了有二十几只，剩下的都被丈夫包了。刚吃完那会儿还意犹未尽，可是到了下午2点，王女士就感觉不对劲了，全身无力，四肢和背部肌肉酸痛，还出了很多汗，没过多久丈夫也出现了类似的不适，只不过症状稍微轻点。夫妻俩眼看着症状越来越重，路也走不动了，赶紧让家里人开车送到中大医院就诊。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>竟是小龙虾致肌溶解</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	“两名患者刚来医院时，还以为是一般的骨关节问题，后来回忆起中午吃了小龙虾，再结合症状和检查结果，诊断为横纹肌溶解。”据中大医院急诊科徐昌盛主治医师介绍，王女士的肌红蛋白、肌酸激酶都高，特别是肌酸激酶超过18000umol/L(毫摩尔每升)，正常值不能高于145umol/L。经对症治疗后，夫妻俩症状明显减轻。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	徐昌盛说，发生横纹肌溶解，最怕肌肉细胞里的肌红蛋白溜到血液里去，并通过肾脏排出体外，由于个头大不能顺利排出就会堵住肾小管，从而引起肾损伤。而且，被破坏的肌肉细胞还会分泌出大量的钾，进入血液中会引起高钾血症，严重时可致心脏骤停危及生命。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	吃小龙虾为什么会引起横纹肌溶解呢？对此，徐昌盛主治医师说，临床上发现横纹肌溶解的患者部分有吃小龙虾的经历，但一群人吃小龙虾可能只有个别人出现问题，而且目前的研究也没有发现任何证据表明与吃的量多量少、重金属超不超标等有关系。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	剧烈运动等也会溶解肌肉\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	除了吃小龙虾，其他一些情况也会引起横纹肌溶解。徐昌盛主治医师指出，肌肉由肌肉细胞组成，运动强度过大时，肌肉会因过度使用而溶解，特别是剧烈运动后又发生中暑，会进一步加重肌肉细胞损伤；醉酒的人如果倒在冰冷的地上不翻身，局部受到压迫又挨冻，血压低循环差，也有肌溶解风险；一氧化碳中毒的人机体本来就缺氧，如果倒在地上躺了一夜，身体受到压迫也很可能引起肌溶解。此外，地震中的挤压伤和一些降血脂、抗病毒的药物也会导致肌溶解，当然这是因人而异的。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	一旦发现全身肌肉酸痛、浑身乏力、小便颜色变深甚至呈酱油色，要警惕是横纹肌溶解，应尽快到医院就诊。如果是吃完小龙虾后出现以上症状，可以先自行催吐，肌溶解患者还要大量喝水以防肾损伤，减少活动。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	[延伸阅读]\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	安全吃龙虾3步骤：\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	1、龙虾一定要清洗干净\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	活龙虾在买来后，最好放在清水里养24—36小时，使其吐净体内的泥沙等杂质，杀死细菌。在加工龙虾时，两鳃里的脏东西要清除，因为鳃毛里面吸附了很多细菌，虾壳最好用刷子刷洗。龙虾细爪的根部最容易藏污纳垢，一定要剪掉。另外要用刷子洗净其身体上的脏物，同时隐藏了大量泥沙和细菌的肠线也必须剔除干净，再用清水充分地清洗2-3次。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	2、煮虾前去头要煮透\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	在被污染的环境中，小龙虾吸附的能力特别强，它有可能把对人体有害的重金属吸附进去，当它吸附重金属元素后，头部会自然产生一个“囊”，把重金属包裹起来。同样，龙虾头部是最易积聚寄生虫的部位，所以吃的时候要去头。要是煮之前能将头去掉最好。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	3、吃虾要有节制\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	吃虾时要有节制，不要一次食用过多，最好10个左右，因为虾是高蛋白食物，部分过敏体质者食用小龙虾会产生过敏症状，如身上起红点、起疙瘩等等，最好不要食用小龙虾。小龙虾是含嘌呤较高的水产品，痛风患者也不要食用。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	通讯员钱钰玲记者戚在兵\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	-相关新闻\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>吃了同一菜场小龙虾</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>9人患肌肉溶解症</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	晨报讯(记者卢斌)6月29号夜里，江苏省中西医结合医院收治了1名患者，据称当晚吃了小龙虾就出现了全身无力的症状，经医院诊断为横纹肌溶解症，目前仍有5人尚未出院。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	在江苏省中西医结合医院肾科病房，南京市民王女士介绍，6月29号，家人在南京和燕路菜场买了1公斤小龙虾，清洗、剪鳃、抽虾线，仔细清洗过再烹制。没想到，晚上6点左右吃的饭，到半夜自己就开始感觉不舒服。王女士说，当时大概是半夜两点，自己突然觉得喘不过气来，仅仅过了一会儿，后背就有一种像刀砍的一样疼痛。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	家人准备把王女士送往医院，可就在从家里下楼的短短几分钟内，情况又严重了。当时王女士整个人都没法站立，到了抢救室后全身疼痛、呼吸不畅、不能自主排尿。医生判断王女士得了横纹肌溶解症。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	记者了解到，王女士入院治疗最早，病情也最严重，抢救过程中，医生曾下了两次病危通知书。目前，王女士病情已经稳定，正在接受进一步治疗。医院方面表示，截至昨天，已有多名患者因为同样症状入院。6月29号至今，十多时间里，先后有9人因吃了同一菜场购买的小龙虾出现肌肉溶解症状，目前仍有5人尚未出院。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	吃小龙虾真的会导致横纹肌溶解症么？专家表示，造成横纹肌溶解症的因素有很多，比如过度运动、高压电电击、毒虫咬伤等，当然也不排除和食用小龙虾有关。目前，就医患者病情已经基本稳定，并未发现患者脏器有损伤。专家表示，只要及时合理治疗，横纹肌溶解症治愈后，不会出现后遗症。\n</p>', '小龙虾，肌溶解', '', 'admin', '0', '1', '2', '2016-07-18 14:45:10', '2016-07-21 16:56:31');
INSERT INTO `articles` VALUES ('46', '14', '你知道吗？这种食物可致鼻咽癌上身', '说起鼻咽癌可能北方地区的人们比较陌生，因为患这种疾病的人群主要集中在广东、广西、湖南、江西和福建等地区。它也属于我国高发的一种恶性肿瘤，鼻咽癌的发病率在五官科发生的肿瘤中居首。其实除了家族原因、地域原因和病毒感染之外，饮食也是导致鼻咽癌发生的一个重要原因。', 'http://o9sccet84.bkt.clouddn.com/20160719180741181.jpg', '', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	今天，我们就和大家一同来了解到底是哪种美食会导致鼻咽癌，以及鼻咽癌会出现地域性的原因，平时针对鼻咽癌又该如何做好预防工作。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	咸鱼（图片来源：网络）\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	这种美食让鼻咽癌发病率增加2-7倍\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	是不是很好奇是什么食物呢？其实今天我们要说的这种美食就是咸鱼，咸鱼以其独特的气味被很多人喜爱，在中国古代咸鱼又被称为鲍鱼，并因为其独特的气味衍生出被鲍鱼之肆这个成语。在广东等地区，咸鱼已经演变为一种特色菜。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	咸鱼致癌原因\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	咸鱼已经被证明是导致鼻咽癌的食物之一，一些调查显示爱吃咸鱼的人患鼻咽癌的风险要高2-7倍。而咸鱼导致鼻咽癌的原因有两方面。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	原因一、亚硝酸盐\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	鱼肉在腌制过程中会产生亚硝酸盐，而亚硝酸盐和鱼肉蛋白质分解的产物胺结合成亚硝胺，其属于一种很强的致癌物质，一次大量或长期摄入这种物质会引发癌症。而人类的鼻咽癌、胃癌、食道癌、肝癌等和这种物质有关。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	原因二、霉变\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	鱼肉属于一种结缔组织少并松散的肉类，细菌容易沿着松散的组织进入肌肉。而且其鱼鳃和内脏中很容易藏匿细菌，这样导致了鱼肉极容易变质，发生霉变。咸鱼出现红色斑点则是发霉现象，这种斑点也被称为赤变。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	霉变咸鱼不仅会导致鼻咽癌，其主要是由于细菌引起的，还可能引起胃肠炎、食物中毒等问题。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	另外，咸鱼中的高盐分会导致胃炎、胃癌、糖尿病、支气管哮喘和上呼吸道感染。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	其他导致鼻咽癌的饮食\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	鼻咽癌的发生具有一定的地域性，如民间会将鼻咽癌称为广东瘤，除了爱吃咸鱼这个原因之外，食物中富含镍元素也是一个原因。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	根据一项在广东地区的调查显示，在鼻咽癌高发地区大米和水中含有的微量元素镍比其他地区高，并且还在鼻咽癌患者的头发中发现镍元素含量高。经过动物实验证明，镍元素对亚硝胺诱发鼻咽癌具有促进作用。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	除此之外，鼻咽癌的发生还和遗传、种族、病毒感染等有关，一般会呈现一种家族聚集现象，如父子或兄弟姐妹同患鼻咽癌等。\n</p>', '你知道吗？这种食物可致鼻咽癌上身', '家庭医', 'admin', '0', '1', '2', '2016-07-19 17:18:45', '2016-07-21 16:56:25');
INSERT INTO `articles` VALUES ('47', '14', '教你如何应对伴随锻炼的六种皮肤问题！', '锻炼对身心健康非常有益，但某些错误会让皮肤病暴发。\n\n美国媒体近日总结了健身方案扰乱皮肤功能的六种常见方式及应对方法。', 'http://o9sccet84.bkt.clouddn.com/20160719172203839.jpg', 'http://o9sccet84.bkt.clouddn.com/20160719172215835.jpg', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>一、肮脏健身设备引起皮肤感染</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	由于每天有很多人使用相同的锻炼器械，病菌和细菌很容易得到传播，这就会导致一些严重的皮肤感染。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	解决方案：只要有可能，在使用任何锻炼器械之前先把它擦干净，即使你认为已经有人这样做过。彻底清洗过双手之后，再接触脸。摸过器械之后再摸脸，会让细菌转移，引发痤疮，甚至是感染。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>二、炎热的环境中引起的痱子</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	湿热会造成汗腺导管堵塞和肿胀，引发痱子。这种皮肤病通常以粉红色（或红色）的小圆点形式出现，看起来类似于粉刺；它最常见于皮肤褶皱处和衣物与皮肤发生摩擦的部位。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	解决方案：如果你打算在户外散步、跑步或骑车，时不时地就停下来找块阴凉地方休息会儿；穿透气的织物，并喝大量的水。在起痱子的区域用凉爽的敷布按压一小会儿，有助于减轻肿胀和不适感。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>三、户外锻炼造成色素沉着过度</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	紫外线暴露会严重损害皮肤，导致胶原蛋白分解，引起皮肤长出细纹和色素沉着，更不用说它会增加人们患上皮肤癌的风险。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	解决方案：如果你打算在户外锻炼，出门之前至少30分钟在所有暴露区域的皮肤涂抹广谱的防晒霜，并经常重复涂抹。皮肤科医生建议人们使用防晒系数在50以上的运动型、防水、广谱的防晒霜，因为它的效果不会因为出汗而减轻，进入眼睛后也不会引起刺痛。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>四、紧身健身服引起身体痤疮</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	在炎热的天气里穿着紧绷的健身服锻炼，会造成汗水和细菌被困在衣服里，这就会刺激皮肤，导致痤疮暴发。汗液会为痤疮细菌的茁壮生长创造出温暖潮湿的环境。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	解决方案：预防身体痤疮的最佳方法是锻炼之后立即淋浴。皮肤科医生建议人们使用含有过氧化二苯甲酰的沐浴露，它能轻柔地去除多余的油脂、细菌、灰尘和其他杂质。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	如果健身房没有淋浴设施，锻炼后立即换上干净的衣服，回到家后立即淋浴。你也可以尝试穿更宽松、更透气的织物，以防止汗水被困在衣服里。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>五、皮肤持续摩擦造成的擦痛</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	当皮肤反复摩擦衣服或身体其他区域时，有时就会长出红色、发炎的斑点，并伴有刺痛和灼热感。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	解决方案：穿光滑、摩擦力小的织物锻炼。如果真的体验到了擦痛，用温水（或凉水）和温和的肥皂清洗这个区域，然后涂抹抗生素软膏，并用纱布覆盖，直到它愈合。你可以尝试使用抗真菌粉末，它能吸收锻炼时身体上多余的水分。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	<strong>六、赤脚淋浴所造成的脚癣</strong>\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	造成脚癣（引起瘙痒、刺痛和脚趾间灼热感的鳞片状皮疹）的真菌存在于任何公共淋浴房的地板上。人们因此而患上甲癣的风险也会升高，这种真菌感染会造成脚趾甲变厚、变脆和褪色。即使是脚底最小的切割伤也会让人乳头状瘤病毒潜入，造成脚底的疣疼痛不已。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	解决方案：淋浴时一定要穿拖鞋，在更衣室里换衣服时也是如此。对拖鞋用消毒剂清洗，并定期更换。如果你担心被真菌所感染了，可以在当地的药房购买非处方的、含有可的松乳霜，它可以清除无害的感染。\n</p>\n<div>\n	<br />\n</div>', '教你如何应对伴随锻炼的六种皮肤问题！', '央广网', 'admin', '0', '1', '2', '2016-07-19 17:22:27', '2016-07-21 16:56:20');
INSERT INTO `articles` VALUES ('48', '15', '这种出汗竟是肝癌征兆', '中国是肝癌发病重灾区，调查发现八成肝癌患者为中晚期。在饮食上如何预防肝癌，哪些是肝癌的早期信号？', 'http://o9sccet84.bkt.clouddn.com/20160719180354746.jpg', 'http://o9sccet84.bkt.clouddn.com/20160719172621926.jpg', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">', '这种出汗竟是肝癌征兆', '39健康', 'admin', '0', '1', '2', '2016-07-19 17:26:24', '2016-07-21 16:56:13');
INSERT INTO `articles` VALUES ('65', '14', '首个预防宫颈癌疫苗获批 9—25岁女性接种', '日前，国内首个预防宫颈癌的人类乳头状瘤病毒（HPV）疫苗获批，用于9到25岁女性的接种，采用3剂免疫接种程序，并有望在明年年初正式上市。为此，记者采访了北京大学肿瘤医院妇瘤科主任高雨农。她认为，宫颈癌的HPV疫苗在国内获批，为广大女性带来福音，不必再去境外接种疫苗。疫苗的安全性有效性不容置疑，请女性朋友放心接种，特别是未发生性行为的女性应尽早接种。', 'http://o9sccet84.bkt.clouddn.com/20160721131508951.jpg', '', '<div class=\"mbArticleSharePic \" style=\"margin:0px auto;\">\n	<img alt=\"国内首个预防宫颈癌疫苗获批 9—25岁接种\" src=\"http://img1.gtimg.com/health/pics/hv1/213/254/2101/136682508.jpg\" />\n</div>\n<p>\n	日前，国内首个预防宫颈癌的人类乳头状瘤病毒（HPV）疫苗获批，用于9到25岁女性的接种，采用3剂免疫接种程序，并有望在明年年初正式上市。为此，记者采访了北京大学肿瘤医院妇瘤科主任高雨农。她认为，宫颈癌的HPV疫苗在国内获批，为广大女性带来福音，不必再去境外接种疫苗。疫苗的安全性有效性不容置疑，请女性朋友放心接种，特别是未发生性行为的女性应尽早接种。\n</p>\n<p>\n	此次国内获批的疫苗只针对HPV—16和HPV—18这两型病毒，称为二价疫苗。高雨农说，人乳头状瘤病毒有上百种类型，其中感染16型和18型病毒的患者所占比重较大。在国外能防止多种类型病毒的宫颈癌的HPV疫苗已经上市多年，比如10价疫苗能防止10种类型的人乳头状瘤病毒，对女性保护更有效。在这方面，我国要尽快开展全国性流行病学调查，摸清妇女感染的主要病毒类型，找到真正的高危病毒类型，推出相应的多价疫苗，更好地保护女性健康。\n</p>\n<p>\n	疫苗用于9至25岁女性接种，其他年龄段接种有用吗？高雨农说，这个年龄段的设定，是假定女性还未发生性行为。接种宫颈癌疫苗，最为有效的是未发生性行为之前。发生性行为，可能会感染病毒，再接种疫苗，保护效果就差。有性行为的女性并非一定感染病毒，接种疫苗依然是有效的。接种疫苗是一级预防，从根本上预防宫颈癌这一致命性疾病。能接种宫颈癌疫苗的，应尽可能地接种。\n</p>\n<p>\n	宫颈癌是我国15岁至44岁女性中的第二大高发癌症，每年约有13万新发病例。每年， 中国的宫颈癌病例占全球的28%以上。因此，我国在开展宫颈癌筛查项目的同时引进HPV疫苗接种，将会显著降低宫颈癌和癌前病变的发病率，从而降低疾病负担。\n</p>\n<p>\n	高雨农说，在我国，宫颈癌发病人数只是从医院统计的接受治疗的人数，实际发生人数还远高于此，发现时多是晚期，错过了最佳治疗期。她建议加大女性宫颈癌的筛查力度，让更多的患者及时被医生发现。\n</p>\n<p>\n	除接种疫苗外，高雨农认为，安全性行为是保护女性免受宫颈癌困扰的前提。一个简单可行的办法是，与自己的性伴侣保持长期、固定的关系，这是最大限度地预防生殖器HPV感染的有效措施。有的国家，男孩都要接种HPV疫苗。全社会要加强安全性行为的教育，防止由此带来的沉重后果。\n</p>', '首个预,宫颈癌,疫苗获', '', 'admin', '0', '0', '2', '2016-07-21 13:15:41', '2016-07-22 09:08:26');
INSERT INTO `articles` VALUES ('66', '14', '老人用药最不安全 记住几个数字避风险', '着老龄化社会的到来，老年人用药安全成为一大问题。2015年，国家药品不良反应监测网络收到药品不良反应/事件报告139.8万份，其中涉及65岁以上老年人报告占了五分之一强，达到21.5%，严重报告比例达到28.7%，两项指标已经连续7年持续上升。近日，国家食药监总局发布《国家药品不良反应监测年度报告(2015年)》，指出尤其需要关注老年患者用药安全问题', 'http://o9sccet84.bkt.clouddn.com/20160721131933888.jpg', '', '<p>\n	随着老龄化社会的到来，老年人用药安全成为一大问题。2015年，国家药品不良反应监测网络收到药品不良反应/事件报告139.8万份，其中涉及65岁以上老年人报告占了五分之一强，达到21.5%，严重报告比例达到28.7%，两项指标已经连续7年持续上升。近日，国家食药监总局发布《国家药品不良反应监测年度报告(2015年)》，指出尤其需要关注老年患者用药安全问题。\n</p>\n<div class=\"mbArticleSharePic \" style=\"margin:0px auto;\">\n	<img alt=\"老人用药最不安全 记住几个数字避风险\" src=\"http://img1.gtimg.com/health/pics/hv1/137/162/2101/136658972.jpg\" />\n</div>\n<p>\n	记住这十个原则避风险——《报告》提醒，老年人因肝肾功能衰退，对药物的耐受性降低，尤其是一些活性较强的药物，极易产生毒副作用和过敏反应。老年用药要遵循十忌原则：一忌随意滥用;二忌品种过多;三忌时间太长;四忌不遵医嘱;五忌生搬硬套;六忌乱用偏方、秘方、验方;七忌滥用补药;八忌更换过频;九忌嗜药成瘾;十忌联用毒副作用强的药物。\n</p>\n<p>\n	记住十三个医院巧咨询——从2014年起，北京市医院管理局将分批在市属医院设立“用药咨询中心”，逐步提高安全合理用药水平。首批设立用药咨询中心的医院包括北京友谊医院、北京同仁医院、北京朝阳医院、北京积水潭医院、北京天坛医院、北京安贞医院、北京世纪坛医院、首都医科大学宣武医院、北京儿童医院、首都儿科研究所、北京中医医院、北京妇产医院、北京老年医院等13家医院。\n</p>\n<p>\n	记住三个妙招很省事——北京协和医院药剂科副主任药师史亦丽在老年病房也经常看到动不动就吃20多种药的老年人。所以在协和医院老年科门诊病房，临床药师是跟着一起出诊的，他们跟医生有一个共同的任务，就是给老年人减药。他们会根据老年人的病情，减去不必要吃的药，留下必须要吃的药。北京协和医院主任药师张继春建议，吃的药物种类最好别超过五种。因为，吃进去的药一般都要通过胃去吸收、肝来代谢、肾脏去排泄，种类过多自然会增加这些器官的负担。除了这个，药物打架还会加重药物对人体的不良反应。一般来说，吃的药物超过5种，不良反应的发生率会到10%，而一旦吃的药物超过了20种，不良反应的发生率就会超过50%!\n</p>\n<p>\n	还有一些老人是忘了吃药。药物种类一多，有的药需要一天吃两次，有的药需要一天吃三次，老人们特别容易迷糊。张继春主任药师就遇到过不少常忘吃药的老年人，她出主意说，慢性病如果有吃一次管一天的药，最好吃这种，尽量使吃药简单化。如果实在没有，必须分几顿吃，最好把用药跟生活习惯联系起来。比如说，如果药物需要睡前吃，那么就把药物放在床头灯的旁边，晚上睡觉前，就能想起来吃药。\n</p>\n<p>\n	有位协和医院的老同志就曾经因为干咳怀疑自己得了癌症，各种检查、CT都做了还是没结果，心情更是压抑。可经过询问之后发现，她的干咳只是因为正在吃的药物不良反应导致的，换药后就好了。因此，拿到药物后，一定要仔细阅读说明书，看看适应症、禁忌症、不良反应、药物相互作用。适应症主要看药物是不是适合自己的情况，禁忌症是指如果自己有上面写的问题坚决不能吃。了解一些药物的不良反应很重要，可以判断自己吃药后出现的问题是不是由于服用药物引起的，免除不必要的麻烦和心理压力。禁用就是指绝对不能吃，慎用是需要咨询一下医生，如果利大于弊就可以服用。药物相互作用可以提醒患者，服用这个药物时跟哪些药物不能一起吃。\n</p>', '老人用药,最不安全 记住,几个,数字避风险', '', 'admin', '0', '0', '2', '2016-07-21 13:19:39', '2016-07-21 16:53:15');
INSERT INTO `articles` VALUES ('49', '16', '眼角有这个是肾虚', '也许你每天都会对镜兴叹：“唉，岁月催人老，脸上的皱纹又多了一条！”其实，它们可能正在向你传达着身体的真实健康状况。', 'http://o9sccet84.bkt.clouddn.com/20160719180811996.jpg', 'http://o9sccet84.bkt.clouddn.com/20160719172957515.jpg', '<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	眉间纹——压力是不是太大？\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	两眉之间的皱纹俗称“恼怒纹”，当人们生气或全神贯注地沉思时，两眉之间就会出现所谓的“恼怒纹”。此外，如果处于身心紧张或重压之下，恼怒纹也会随之而来。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	【驱“纹”法】1.每天5至10颗甜杏仁，它能帮助你提高注意力，并且缓解紧张的情绪。2.晚间腹部呼吸10次。让自己自然放松，身体尽量平躺，紧闭双目，做腹式呼吸10次。这个动作可以明显改善你紧绷了一天的身心。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	额头纹——睡眠好不好？\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	额头纹是检验你睡眠质量的一个标准。中医认为，额头纹突现是由长期睡眠不足引起的。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	【驱“纹”法】 1.在你的枕头上滴上几滴薰衣草精油，你就能好梦一晚。2.柠檬能起到镇定作用。此外，大米、燕麦等谷物外部谷皮均含有可帮助改善睡眠以及维持镇定的血清素。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	鱼尾纹——肾虚？\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	中医认为，人体肾脏在脸部的反射区是在眼外角与耳朵相连接的部位，眼角有很深的鱼尾纹，就该考虑一下是不是肾脏出现了问题。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	【驱“纹”法】1.内养：大枣补气养血，桂圆补血，枸杞滋补肾阴，核桃补肾强腰，还有花生、板栗等也都是很好的补肾补血类健康零食。2.外敷：两手掌搓热后，分别放于腰部，至身体有微热的感觉为止，可以补纳肾气。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	唇侧纹——肝胆不妙？\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	中医认为，唇部右边的皱纹透露出来的是肝脏和胆囊的负担过重，左边的皱纹则可能是显示脾脏有问题的信号。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	【驱“纹”法】1.肝脏在凌晨1至3时之间活动最为活跃，这也是肝脏正在排毒的时间。如果在此时间段休息不好，就会直接影响肝脏健康。2.养肝 的最好食物绝对不是很多人以为的猪肝，而是鸡肝，它的补肝作用更强，还可温胃。3.醋味酸而入肝，具有平肝散淤、解毒抑菌等作用。每日可取食醋40毫升， 加温水冲淡后饮服。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	鼻皱纹——心脏好不好？\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	鼻皱纹代表的器官是心脏。鼻梁部位皱纹多而密，很可能是心脏负担过重的原因。\n</p>\n<p style=\"font-family:宋体, Arial, sans-serif;font-size:15.9996px;text-indent:2em;background-color:#FFFFFF;\">\n	【驱“纹”法】1.红葡萄酒每天一点点。为了增强心脏功能，可每天饮用少量红葡萄酒，它被誉为最佳的心脏滋补饮料。2.增加叶酸的摄入量有助于控制血压，绿叶蔬菜、谷类食品以及强化面包中含有的叶酸，对患有中风和心脏病的人均有好处。3.腰围和臀围的比值是衡量患心脏病风险的最佳指标。用腰围数除以臀围数，男性应该不高于0.9，女性则不高于0.850，得出的数值越高，患心脏病的危险就越大。\n</p>', '眼角有这个是肾虚', '中国新闻网', 'admin', '0', '1', '2', '2016-07-19 17:30:00', '2016-07-21 16:56:07');
INSERT INTO `articles` VALUES ('58', '4', '法国读硕博就业机会多', '【导读】已连续举办14年的中国国际教育展于10月25-26日在北京国家会议中心拉开帷幕，逾40个国家约600所海外院校齐聚一堂，与中国的学生及家长热情交流，详细介绍其院校特色、录取条件等。据了解，和以往相比，今年展会最大亮点是各个主流留学目的地国家的使馆、权威教育机构和大学等纷纷借力新媒体手段，强力推出官网、微博、微信和APP，招生方式更趋向于多元化。', 'http://o9sccet84.bkt.clouddn.com/20160720101311598.jpg', 'http://o9sccet84.bkt.clouddn.com/20160720101317360.jpg', '<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	周子牧（法国驻华大使馆文化教育合作处参赞）：今年我们是一个非常特别的展团，因为有30多所法国高校来这边参加展会，有工程师学校，还有高等商学院，当然了，还有一些关于美食和艺术方面的学校。\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	周子牧（法国驻华大使馆文化教育合作处参赞）：中国留学生是法国的第二大生源国，基本上跟摩洛哥的留学生人数差不多，所以对于中国留学生我们有一个非常开放的政策。我们高等教育署不仅在北京，在中国很多城市都设立有中心。法国高等教育署在中国的团队，在全球是最大的团队，同时，一旦中国留学生到达法国以后，我们有非常快捷的渠道给他们发放签证。\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	周子牧（法国驻华大使馆文化教育合作处参赞）：有越来越多的中国学生赴法国留学，每年大概有1万名中国学生去法国留学。现在在法国就读的中国留学生人数大概是35000到40000人，在欧洲大陆，法国是第一大中国留学生接待国。有很多中国留学生，去法国就读商校，管理类的院校，但是工程师院校也吸引了越来越多的中国留学生。我们注意到，每年有2/3的中国留学生去法国就读商校和工程师类的院校。\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	我们也注意到就近两年有越来越多的中国留学生去法国就读以英语授课的专业，在去年，法国国会通过了一项法律，旨在鼓励法国的高校向学生提供以英文授课的专业。这样的话，就会有越来越多的中国留学生可以去法国就读以英文授课的专业。\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	周子牧（法国驻华大使馆文化教育合作处参赞）：我们学生获得了文凭以后，他们会有一年的时间，去寻找工作。然后当他们找到工作以后，他们需要申请一个居留证。目前，在法国和在欧洲都是一样的，就业前景不是特别明朗。\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	<span>但是对于中国留学生，因为有2/3的中国留学生去法国就读硕士或者是博士级别的专业，所以说对于这些中国学生来说，他们在法国毕业了以后，找到一份工作的机会还是非常大的，因为法国的企业都非常欢迎这样的毕业生。而且在法国政府也采取了很多措施，希望能够鼓励这些留学生在法国就业，所以对这些硕士和博士级别以上的学生来说，是没有什么问题的。</span>\n</p>', '法国读硕博就业机会多', '凤凰网教育频道', 'admin', '0', '1', '2', '2016-07-20 10:13:43', '2016-07-21 16:55:32');
INSERT INTO `articles` VALUES ('59', '4', '专访波莫纳学院校长：博雅教育培养未来领袖', '亚里士多德将教育分为“自由的教育”与“偏狭的教育”。亚里士多德认为，自由教育是以人的理性的自由发展和德性的完善为最高目标的教育，“我们全部生活的目的应是操持闲暇……在闲暇中，人们发展自己的理性，操修善德，以造就豁达的胸襟和自由的精神。”这就是博雅教育的思想起源。', 'http://o9sccet84.bkt.clouddn.com/20160720101555607.jpg', 'http://o9sccet84.bkt.clouddn.com/20160720101559568.jpg', '<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	<br />\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	博雅教育在美国叫Liberal Arts Education，它起源于古希腊，本义是“培养通达智能的自由公民教育”。从教育的内容而言，<strong>博雅教育实质上是一种以“人的精神道德修养”为内核，同时又将“人格修养”与“知识积累”相结合的教育方法与过程。</strong>\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	美国波莫纳学院校长David Oxtoby就是一位博雅教育的有力倡导者，他认为<strong>博雅教育就是“全球化、创新型、具备批判性思维”的自由教育</strong>，“它试图通过人的性格培养来促进个性发展。”他出生于博雅教育世家，父母都是美国文理学院的大学教授，从小耳濡目染，让他对博雅教育充满了感情。他经常在世界各地宣讲这种独特的教育模式的好处。另外，他也是美国高等教育界一位倍受尊重的领导者，曾担任芝加哥大学物理科学院院长，哈佛大学监督委员会主席。\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	凤凰网教育频道记者受邀对David Oxtoby校长进行了独家专访，采访过程中，他不止一次谈到文理学院的独特优势，<strong>“博雅教育培养的学生不仅具有非常广泛的知识结构，而且要求他们学会融会贯通，要在某一领域进行深度钻研。”</strong>\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	美国的博雅教育，最初是按照英国剑桥大学和牛津大学的传统教育模式而设计的。在美国早期的常青藤盟校，即1636年建校的Harvard College（今哈佛大学）、1701年建校的Yale College（今耶鲁大学）、1746年建校的College of New Jersey（今普林斯顿大学）、1755年建校的The College of Philadelphia（今宾夕法尼亚大学）、1764年建校的College of Rhode Island（今布朗大学）等，也都承袭了英国大学，特别是牛津和剑桥大学的传统，以修习七艺（文法、逻辑、修辞、几何、天文、算术、音乐）为核心的古典博雅教育（Liberal Education）来培养有教养的自由人。博雅教育目前已成为美国教育的特色，很多小型文理学院以经典、出色、高质量的本科教育受到越来越多学生的欢迎，逐渐成为美国高等教育体系最璀璨的明珠。<strong>虽然全美只有3% 左右的美国学生有幸进入文理学院，然而19% 的美国总统、20% 的美国国家科学院院士的本科都是毕业于文理学院的。</strong>\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	<strong>\n	<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n		<strong>古老的博雅教育在21世纪继续崛起，源于它所倡导的解决问题的能力和创新精神，让学生成长为解决世界上最具挑战性的问题的全球公民。</strong>作为一个在全球推广博雅教育的领军性人物，David Oxtoby坦言，其实很多人对“博雅教育”有误解，“在美国和在中国一样，向人阐释博雅教育究竟是什么都是一个挑战。”但他依旧乐此不疲，依旧热心于将博雅教育的理念传播到世界各地。他也惊喜地发现，在亚洲地区已经有越来越多的人认识到博雅教育的独特魅力。\n	</p>\n	<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n		谈到中国的素质教育，他提到了史提夫乔布斯的名言“把技术和博雅学科结合在一起，能够有非常美妙的事情发生。”他认为，中国在技术方面一直都很强势，如果博雅教育能够更好的发展，那么一定也会结出让人怦然心动的果实。\n	</p>\n<br />\n</strong>\n</p>', '专访波莫纳学院校长：博雅教育培养未来领袖', '凤凰教育', 'admin', '0', '1', '2', '2016-07-20 10:16:03', '2016-07-21 16:55:28');
INSERT INTO `articles` VALUES ('60', '4', '复旦大学校长许宁生：追求价值观的不断完善与提升', '7月3日上午，复旦大学2015届本（专）科毕业典礼举行。中科院院士、复旦大学校长许宁生发表了演讲，他寄语全体毕业生，希望复旦的同学们在今后人生中要常怀感恩之心，追求自身价值观的不断完善与提升，牢记“博学而笃志，切问而近思”校训，继承发扬“团结、服务、牺牲”的复旦精神，追求梦想，为祖国繁荣发展和人类文明进步作出自己的贡献！他还叮嘱同学们常回家看看，复旦永远是你们的精神家园。', 'http://o9sccet84.bkt.clouddn.com/20160720101829185.jpg', 'http://o9sccet84.bkt.clouddn.com/20160720101833550.jpg', '<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	<br />\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	许宁生说，同学们毕业后有些将深造，有些将入职业界；有些将远涉海外，有些将奔赴祖国各地。无论你们将做什么，你们走向何方，你们永远是母校的牵挂。正是因为这份牵挂，我珍惜这次代表学校与你们短暂交流的机会。然而，千言万语说不尽，我想，大学留给大家最宝贵的是精神财富，借此机会，与你们共享几个想法。\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	复旦人是懂得感恩的。刚才，毕业生代表的发言，引发了我的共鸣。每个人在成长的道路上都不是孤立存在的个体，你们的每一次成功和每一点进步，固然得益于自身的禀赋和努力，然而不能忘记父母、师长、朋友、学校在支撑着你们，不能忘记这个蓬勃发展的国家和日新月异的时代在托举着你们。我知道你们此刻思绪万千，满怀对国家、社会、母校、老师、父母、亲人、朋友、同学等的感恩之心，而我想特别提醒的是，家人是你们最先应该感谢的对象。如果能够想到把毕业后第一份劳动所得与家人分享，那你们将不会留下一辈子的遗憾！\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	复旦人追求自身价值观的不断完善提升。“爱国、敬业、诚信、友善”是处理人际关系的核心价值观，尽管你们已经熟记心中，但你们还要不断努力，坚守一生！爱国，就是要像复旦校歌所唱的那样：“巩固学校维护国家，先忧后乐交相勉。”把推动祖国繁荣发展和人类文明进步作为自己的终身使命。敬业，就是要恪尽职守、敬岗爱业、精益求精，在不同的岗位上努力工作。诚信，就是要持身以正，待人以诚，做光明磊落、堂堂正正的复旦人。友善，就是要谦虚礼让、宽容善良，善于团结协作，共同进步。\n</p>\n<p style=\"text-indent:28px;font-size:14px;color:#2B2B2B;font-family:simsun, arial, helvetica, clean, sans-serif;background-color:#FFFFFF;\">\n	复旦人珍惜校训蕴含的精神财富。“博学而笃志，切问而近思”，内涵十分丰富，复旦人应予铭记并不断为其增添光彩！同学们，这个时代为你们创造了前所未有的发展机遇，同时也对你们有了更高的期待和要求。请你们守训笃实，奉献社会，奉献国家，奉献文明进步事业。复旦向来是孕育爱国知识分子的沃土，从建校开始就与祖国同命运、共进退。回顾百十年校史，一代又一代复旦人在祖国需要的地方用知识、汗水乃至生命践行着复旦校训。在座的各位毕业生即将奔赴世界各地、五湖四海，希望你们继承和发扬“团结、服务、牺牲”的复旦精神，坚定信念，无畏困难，敢于担当，在人生的道路上不断进步，做一位无愧于时代的复旦人！\n</p>', '复旦大学校长许宁生：追求价值观的不断完善与提升', '复旦大学', 'admin', '0', '1', '2', '2016-07-20 10:18:36', '2016-07-26 15:09:58');
INSERT INTO `articles` VALUES ('61', '2', '资阳失联孕妇向家人报平安 称心里苦想出去闯一闯', '13日，资阳(微博)安岳27岁孕妇黄晓翠从成都乘坐大巴车回老家安岳途中失去联系；18日，安岳警方通报，黄晓翠在当日乘车抵达安岳后，已于当晚返回成都（成都商报连续报道）。18日晚23时许，黄晓翠主动联系上警方，并向家人报了平安。她告诉父亲杨付军：因为心里很苦，家人理解不了，不甘人生就此结束，所以想出去闯一闯。然而，她没有想到事情闹得这么大。昨日，黄晓翠家人已前往派出所撤销了报案。', 'http://o9sccet84.bkt.clouddn.com/20160720143337329.jpg', 'http://o9sccet84.bkt.clouddn.com/20160721134635230.jpg', '<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	13日，资阳(<a class=\"a-tips-Article-QQ\" href=\"http://t.qq.com/vziyang#pref=qqcom.keyword\" target=\"_blank\">微博</a>)安岳27岁孕妇黄晓翠从成都乘坐大巴车回老家安岳途中失去联系；18日，安岳警方通报，黄晓翠在当日乘车抵达安岳后，已于当晚返回成都（成都商报连续报道）。18日晚23时许，黄晓翠主动联系上警方，并向家人报了平安。她告诉父亲杨付军：因为心里很苦，家人理解不了，不甘人生就此结束，所以想出去闯一闯。然而，她没有想到事情闹得这么大。昨日，黄晓翠家人已前往派出所撤销了报案。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	昨日，杨付军在电话中告诉成都商报记者，18日晚，女儿黄晓翠已和警方取得联系。“她换了手机号码，我马上给她发了多条短信，她也回了。”杨付军说，女儿在电话里告诉他，此次离开是担心在厂里负责的工作做不好，所以想到北京继续学美容，“她说我身体不好，担心给我说了她的想法会被我骂，所以一个人走了。”\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	在写给父亲的短信中，黄晓翠说：“我是爱你们的，但是我心里很苦，无论如何你们也理解不了……这些天我一直惴惴不安，但是我没有办法回头，我不想再过从前的生活，无论外面的路多难走，我想出来闯一闯，失败也好，成功也罢……别再通过别人找我，我的离开完全是自己的决定，你和妈妈永远都是我的牵挂，望珍重。”\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;text-indent:2em;background-color:#FFFFFF;\">\n	“她说，她也没想到事情闹这么大。”杨付军说，他本来想狠狠骂女儿，但女儿说了一会便哭起来，所以他也心软了，并未责怪女儿。经历了这次风波后，杨付军表示：“既然她对继承家里的木门生意不感兴趣，那还是听她的吧。”杨付军决定让女儿去闯一下，并对她在资金上给予支持。\n</p>', '孕妇，失联', '', 'admin', '0', '1', '2', '2016-07-20 10:51:53', '2016-07-21 16:55:19');
INSERT INTO `articles` VALUES ('62', '4', '工业机器人产业迅猛发展 专业人才缺口巨大', '随着我国人口红利逐渐消失，加上劳动力逐年减少、人工成本的上涨、工作环境的改变、人口老龄化和多元化的市场竞争，使各企业面临着重重压力。为了解决困境，现在越来越多的企业把目光瞄准了工业机器人及自动化设备，以此代替人工生产，并且机器人被应用到了各行各业。', 'http://o9sccet84.bkt.clouddn.com/20160720131909540.jpg', 'http://o9sccet84.bkt.clouddn.com/20160720131922394.jpg', '<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	随着我国人口红利逐渐消失，加上劳动力逐年减少、人工成本的上涨、工作环境的改变、人口老龄化和多元化的市场竞争，使各企业面临着重重压力。为了解决困境，现在越来越多的企业把目光瞄准了工业机器人及自动化设备，以此代替人工生产，并且机器人被应用到了各行各业。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  《中国制造2025》提出要“围绕汽车、机械、电子、危险品制造、国防军工、化工、轻工等工业机器人、特种机器人，以及医疗健康、家庭服务、教育娱乐等服务机器人应用需求，积极研发新产品，促进机器人标准化、模块化发展，扩大市场应用。”工信部《关于推进工业机器人产业发展的指导意见》更是细化了具体战略目标：“到2020年，形成较为完善的工业机器人产业体系，培育3-5家具有国际竞争力的龙头企业和8-10个配套产业集群；工业机器人行业和企业的技术创新能力和国际竞争力明显增强，高端产品市场占有率提高到45%以上，机器人密度(每万名员工使用机器人台数)达到100以上，基本满足国防建设、国民经济和社会发展需要”。无论是从顶层设计还是具体产业规划，机器人产业都受到了极大的关注与支持，发展目标和路径清晰，发展前景广阔。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  伴随着机器换人浪潮的到来，对于相关人才的需求也日益扩大。机器人始终需要人来操作、维护、保养。人和机器的关系属于一种控制链的关系，因此只有将人与机器进行协同合作才能达到人机合一的效果，为企业创造更高的工作效率，所以机器人产业的壮大又为机器人服务人才制造了新的市场机会。国家相关调研数据来看，机器人项目的增长速度与专业人才的需求存在很大的缺口，全国范围内的人才缺口已达上百万人。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  2016年，四川科技职工大学和成都环龙智能系统设备有限公司采用校企合作的模式，共同打造工业机器人技术专业，旨在培养具备工业机器人制造、应用、维护等方面的理论知识和专业技能，能从事工业机器人系统仿真、集成设计、编程、调试、操作、销售，以及智能自动化生产系统操作、维护、改造及管理等工作的高素质技能型人才。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  据四川科技职工大学招生就业处负责人介绍，该专业有三个特点：一是就业容易，待遇较高，目前相关企业给出的普遍待遇标准为：试用期3500----5000元/月，转正后4000—7500元/月。二是该专业采用的是“双元制”教学模式，学生学习过程比较轻松。三是在学习专业技能的同时，学生还可通过成教、自考等途径取得国家承认学历的专本文凭。\n</p>', '工业机器人，专业人才', '', 'admin', '0', '1', '2', '2016-07-20 13:19:26', '2016-07-21 16:55:15');
INSERT INTO `articles` VALUES ('63', '4', '四川本一批录取创新高 19日本一批征集志愿', '考生家长龙先生：听说今年的本一批录取接近尾声了，今年的录取调档线有好高？如果需要填报征集志愿，该做好哪些准备？', 'http://o9sccet84.bkt.clouddn.com/20160720132225933.jpg', 'http://o9sccet84.bkt.clouddn.com/20160720132237723.jpg', '<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	考生家长龙先生：听说今年的本一批录取接近尾声了，今年的录取调档线有好高？如果需要填报征集志愿，该做好哪些准备？\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  18日上午，省高考录取场举行了第二次媒体开放日，对外公布了本一批次院校在川录取情况：从投档情况来看，本一批次北大理科调档线695分、清华文科调档线642分，分别比今年我省本一批分数线（理科532，文科540）高出163分和102分，居理科、文科调档线榜首。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  因为参加本一批次招生的院校主要是教育部直属高校、“985”、“211”工程建设院校、部省共建的原部委属重点高校、经核准参加本批录取的高校和专业，所以本批次录取情况备受家长和考生关注。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  <strong>985、211院校调档线普遍偏高</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  省考试院副院长魏成松介绍，截至18日上午，我省共完成了艺术类本科提前批、普通本科提前批、艺体本科批次、国家专项计划、地方专项计划的录取工作。经过3次摸拟投档后，我省本科一批院校考生电子档案于17日下午正式向全国在川招生高校投放。在本科一批平行志愿一轮投档后：文科212所院校投档满额，仅4所院校缺额13名；理科272所院校投档满额，仅20所院校缺额672名。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  从投档情况来看，文科调档线排名前5的院校分别是清华大学642分、北京大学638分、上海交通大学633分、复旦大学627分、中国人民大学625分；理科调档线排名前5的院校（部）分别是北京大学695分、清华大学689分、复旦大学686分、上海交通大学686分、北京大学医学部677分。今年，“985”、“211”院校特别是地处北京、上海等发达地区的高校，与往年一样，考生报考热度持续推高，学校生源均十分充裕，一轮投档后均能全面完成计划，各校调档线普遍偏高。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  <strong>省属院校 生源富足</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  参加本科一批录取的部委属院校平行志愿一轮投档后生源全面告捷，参加本科一批录取的省外地方属院校平行志愿一轮投档后：文科119所院校计划一次性满额，理科166所院校计划一次性满额。参加本科一批录取的省属院校平行志愿一轮投档后：文科13所院校计划一次性满额，理科10所院校计划一次满额。特别是西南科技大学、西南石油大学、西华大学、成都理工大学、成都信息工程大学、成都中医药大学等均有较富裕的生源。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  <strong>特别提醒19日本一批征集志愿</strong>\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  按照录取工作的安排，7月19日，本一批（含国家专项、地方专项计划）将征集志愿，录取场负责人提醒考生务必及时在省教育考试院官网查阅，符合条件的考生应按时在网上填报征集志愿的学校及专业。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  一位招生工作人员提醒，考生前往现场咨询，需要带上准考证和身份证进行登记。\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  另外，省录取场提醒考生，除了前往现场咨询，也可以拨打查询电话：028-82791382、82791383、\n</p>\n<p style=\"font-size:16px;font-family:宋体, Arial, sans-serif;background-color:#FFFFFF;\">\n	  82791384、82791385、82791386、82791387、82791388。录取期间监督及举报电话：028-82791252。\n</p>', '四川，高考录取', '', 'admin', '0', '1', '2', '2016-07-20 13:22:41', '2016-07-21 16:55:10');
INSERT INTO `articles` VALUES ('67', '2', '特想告诉你！神奇的藿香正气水可治8小病', '夏季天气炎热，容易中暑，很多家庭常备藿香正气水来解暑。除了大家公认的解暑效果好以外，藿香正气水还有很多你意想不到的功效。', 'http://o9sccet84.bkt.clouddn.com/20160721153101708.jpg', '', '<p>\n	夏季天气炎热，容易中暑，很多家庭常备藿香正气水来解暑。除了大家公认的解暑效果好以外，藿香正气水还有很多你意想不到的功效。\n</p>\n<div class=\"mbArticleSharePic \" style=\"margin:0px auto;\">\n	<img alt=\"特想告诉你！神奇的藿香正气水可治8小病\" src=\"http://img1.gtimg.com/health/pics/hv1/219/194/2090/135951939.jpg\" />\n</div>\n<p>\n	中药藿香正气水可治8小病\n</p>\n<p>\n	1、蚊虫叮咬：用适量的藿香正气水外搽患部3—5分钟，很快就能消除瘙痒。\n</p>\n<p>\n	2、晕车晕船：可用医用棉签蘸取藿香正气水，直接外涂肚脐，或将药液敷于肚脐内，有一定的预防作用。\n</p>\n<p>\n	3、空调病：夏天冷风吹多了，也会受寒感冒，或腹痛、腹泻。内服各种剂型的藿香正气均可。\n</p>\n<p>\n	4、水土不服：夏天是旅游的高峰期，外出时如果水土不服，出现消化不良、呕吐、腹泻等胃肠道不适，带上一盒“藿香正气”有备无患。藿香正气水（液）具有解表化湿、理气和中的作用，出现上述症状时，每次服10毫升，每天3次，能够缓解症状。\n</p>\n<p>\n	5、美尼尔综合征：可用10毫升藿香正气水（液），每天服3次，连用3—5天。症状改善后，再用六君子丸调理。\n</p>\n<p>\n	6、小儿消化不良：消化不良常与“湿病”有关。为消食和胃药加点健脾化湿药，事半功倍。方法是：取山楂、神曲各6克，麦芽10克，水煎去渣，再加入藿香正气水20毫升，分3次服，连用3天。\n</p>\n<p>\n	7、女性白带过多：藿香正气水10毫升，每天3次，连服5—7天。\n</p>\n<p>\n	8、湿疹：用藿香正气水外涂皮损处，每天3—5次，连用3—5日。对带状疱疹也有效。\n</p>\n<p>\n	藿香正气水的3大禁忌\n</p>\n<p>\n	1、服用后不要立即驾车。\n</p>\n<p>\n	服用藿香正气水后，其中的乙醇没有挥发，如被交警检测，会检出乙醇超标，误认为酒后驾车。因此服药后至少半小时再驾车，并多张口呼吸，尽快使乙醇挥发。\n</p>\n<p>\n	2、乙醇过敏者慎服藿香正气水。\n</p>\n<p>\n	对乙醇过敏的人服用了藿香正气水会出现过敏反应，出现全身发热、心跳加快、皮肤潮红、丘疹、瘙痒、烦躁不安、血压降低等症状。\n</p>\n<p>\n	3、用先锋霉素时慎用藿香正气水。\n</p>\n<p>\n	先锋霉素和乙醇会产生双硫醒样反应，出现脸部发热、潮红、头痛、出汗、血压下降、烦躁不安、视物模糊等症状，严重的还会出现休克、惊厥。\n</p>', '特想告诉，你！神奇的藿香正，气水，可治8小病', '', 'test', '0', '1', '2', '2016-07-21 15:31:16', '2016-07-21 16:53:10');
