-- ----------------------------
-- Table structure for `issues`
-- ----------------------------
DROP TABLE IF EXISTS `issues`;
CREATE TABLE `issues` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增ID',
  `video_id` int(11) NOT NULL COMMENT '视频ID',
  `name` varchar(20) COLLATE utf8_unicode_ci NOT NULL COMMENT '期数',
  `summary` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '文章摘要',
  `url` varchar(300) COLLATE utf8_unicode_ci NOT NULL COMMENT '视频链接地址',
  `views` int(11) NOT NULL DEFAULT '0' COMMENT '播放次数',
  `status` int(11) NOT NULL DEFAULT '0' COMMENT '状态（0-创建未审核，1-已审核，2-已发布',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

