-- ----------------------------
-- Table structure for `system`
-- ----------------------------
DROP TABLE IF EXISTS `system`;
CREATE TABLE `system` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增ID',
  `webtitle` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '网站标题',
  `keywords` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '关键词',
  `description` text COLLATE utf8_unicode_ci NOT NULL COMMENT '网站描述',
  `img` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '网站logo',
  `weixin` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '网站微信二维码',
  `weibo` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '网站微博二维码',
  `weixinurl` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '网站微信链接',
  `weibourl` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '网站微博链接',
  `icp` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '网站备案编号',
  `tongji` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '网站统计代码',
  `about` text COLLATE utf8_unicode_ci NOT NULL COMMENT '关于我们',
  `contact` text COLLATE utf8_unicode_ci NOT NULL COMMENT '联系我们',
  `follow` text COLLATE utf8_unicode_ci NOT NULL COMMENT '关注我们',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
-- Records of system
-- ----------------------------
INSERT INTO `system` VALUES ('1', '中国教育网络电视台健康台-四川频道', '健康，养生', '', 'http://o9sccet84.bkt.clouddn.com/20160719171935880.png', 'http://o9sccet84.bkt.clouddn.com/20160719172420533.jpg', 'http://o9sccet84.bkt.clouddn.com/20160719172427509.jpg', 'http://www.honcte.com/', 'http://www.honcte.com/', '', '', '<h3>\r\n	<span style=\"color:#747474;font-family:微软雅黑;font-size:12px;font-weight:normal;line-height:1.5;\">在实现中国人“营养梦 健康梦”这个目标的过程中，媒体扮演着不可或缺的角色。健康传播需要大众媒体的积极参与，需要及时创新传播渠道，探索有效的传播模式。人民网是《人民日报》建设的以新闻为主的大型网上信息交互平台，是人民日报社控股的传媒文化上市公司，也是国际互联网上最大的综合性网络媒体之一。中国营养学会是党和国家联系我国营养科技工作者的桥梁和纽带，是发展中国营养科学技术事业的重要社会力量。致力于为促进营养科技发展服务，为经济社会发展服务，为提高全民营养科学素质服务，为营养科学技术工作者服务。双方基于各自优势资源，在人民网建立“人民营养家”大型专栏，兼具政策知识解读、知名营养家和科普传播、行业动态发布、营养健康产业公益活动、舆情分析研判等功能。双方的合作，不仅是人民网权威性、公信力、大众化的集中体现，也是中国营养学会专业性、敬业性、前沿性的一个良好展示。“中国教育网络电视台健康台-四川频道”期待社会各界的参与，让你我共同携手，使之成为人民正确获得营养知识的最佳平台。</span> \r\n</h3>', '<h3>\r\n	<span style=\"color:#747474;font-family:微软雅黑;font-size:12px;font-weight:normal;line-height:1.5;\">中国教育网络电视台健康台-四川频道坐落在美丽的天府之国——成都，运行了99健康网(www.99.com.cn)，网站于2016年，是一家以提供健康资讯、保健养生、疾病预防、疾病数据查询、医患交流为主的健康门户网站。</span> \r\n</h3>', '<img src=\"/admin/kindeditor/attached/image/20160719/20160719172744_40190.png\" alt=\"\" /> <img src=\"/admin/kindeditor/attached/image/20160719/20160719172829_11362.png\" alt=\"\" />', '2016-07-18 14:36:31', '2016-07-22 09:57:15');

-- ----------------------------
-- Table structure for `users`
-- ----------------------------
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `email` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `password` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `remember_token` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `type` int(2) NOT NULL COMMENT '管理员权限',
  PRIMARY KEY (`id`),
  UNIQUE KEY `users_email_unique` (`email`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

