DROP PROCEDURE IF EXISTS `edit_articles`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `edit_articles`()
BEGIN
UPDATE articles SET views='0';
END
;;
DELIMITER ;

-- ----------------------------
-- Event structure for `sj_articles`
-- ----------------------------
DROP EVENT IF EXISTS `sj_articles`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` EVENT `sj_articles` ON SCHEDULE EVERY 1 MINUTE STARTS '2016-07-26 16:17:26' ON COMPLETION NOT PRESERVE ENABLE DO call edit_articles()
;;
DELIMITER ;
